-- NPC: npc-arasaka-chief-compliance-2080-2093
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-arasaka-chief-compliance-2080-2093', 'Юрико Танака — Chief Compliance Officer Arasaka (2080–2093)', '{"metadata": {"id": "npc-arasaka-chief-compliance-2080-2093", "title": "Юрико Танака — Chief Compliance Officer Arasaka (2080–2093)", "document_type": "canon", "category": "narrative", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-13T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "arasaka", "corporate"], "topics": ["corporate-compliance", "legal-intrigue", "narrative-characters"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "canon-factions-arasaka-scenarios-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka/chief-compliance-officer-2080-2093.md", "visibility": "internal", "audience": ["concept", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо зафиксировать ключевую фигуру Arasaka, управляющую комплаенсом в 2080–2093 годах.", "goal": "Сконсолидировать биографию, мотивацию, связи и сценарные роли Юрико Танаки.", "essence": "Хладнокровная юридическая стратег Arasaka, сохраняющая порядок внутри корпорации и контролирующая юридические риски.", "key_points": ["Карьерный путь от корпоративного юриста до Chief Compliance Officer.", "Создатель «Кодекса Танаки» и глобальной системы комплаенса.", "Источники квестов по аудиту, корпоративным интригам и этическим дилеммам."]}, "content": {"sections": [{"id": "identity", "title": "Основная информация", "body": "• Имя: Юрико Танака (Yuriko Tanaka), род. 2013, возраст 67 лет в 2080 г.\\n• Роль: Chief Compliance Officer Arasaka, член расширенного совета директоров.\\n• Прозвища: «Железная Леди Параграфов», «Танака-сан с Несгибаемой Волей».\\n• Локация: Arasaka Tower, 88-й этаж. Служит символом корпоративного порядка.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешний вид и импланты", "body": "• Строгий японский деловой стиль, высокие стандарты внешнего вида и минимум украшений.\\n• Использует Kiroshi Optics «Legal Eagle», нейропроцессор «Themis Protocol», ассистент «Silent Authority», модуль контроля эмоций «Equilibrium», субдермальную защиту и модификатор голоса «Authority Tone».\\n• Кибернетизация ~25%, акцент на когнитивные устройства.\\n", "mechanics_links": [], "assets": []}, {"id": "biography", "title": "Биография и карьера", "body": "• 2013–2035: образование в юридическом факультете под контролем Arasaka, ранний опыт защиты семьи от ложных обвинений.\\n• 2035–2060: рост от младшего юриста до директора комплаенса, участие в юридических кризисах после Четвёртой корпоративной войны.\\n• 2065–2080: заместитель главного юридического советника, автор «Кодекса Танаки».\\n• 2080–2093: назначена Chief Compliance Officer, выстраивает глобальную систему соответствия, защищает корпорацию от международных претензий.\\n• Репутация: выиграла все значимые судебные споры, воплощение корпоративной непреклонности.\\n", "mechanics_links": [], "assets": []}, {"id": "motivations", "title": "Мотивация, цели, страхи", "body": "• Верит в порядок и верховенство правил, служит идее корпорации как института.\\n• Долгосрочные цели: создать международный стандарт комплаенса, защитить Arasaka от юридических угроз, расширить присутствие корпорации.\\n• Страхи: внутренняя коррупция, разрушение созданной системы, международные трибуналы.\\n• Желания: увековечить «Кодекс Танаки», воспитать преемника, укрепить статус Arasaka как оплота порядка.\\n", "mechanics_links": [], "assets": []}, {"id": "personality", "title": "Характер и поведение", "body": "• Несгибаемая воля, перфекционизм, хладнокровие, лояльность идее корпорации.\\n• Сильные стороны: память, аналитика, иммунитет к манипуляциям; слабости: жёсткость, одиночество, непонимание хаотичных людей.\\n• Манеры: медленная речь, выверенные движения, зрительный контакт, жест перебирания серёг в стрессовых ситуациях.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Отношения и влияние", "body": "• Ханако Арасака — уважение и верность; сыграла роль в свержении Ёринобу.\\n• Отдел безопасности — напряжённое сотрудничество, обеспечивает юридическое прикрытие их операций.\\n• Militech — профессиональный антагонизм; NetWatch — осторожное сотрудничество; Kang Tao — дипломатические контакты.\\n• Игнорирует банды, взаимодействует с NCPD через юридические каналы.\\n", "mechanics_links": [], "assets": []}, {"id": "quest-hooks", "title": "Квесты и сценарные роли", "body": "• «Чистые руки»: аудит директора, требует легальных методов, развилка между честностью, саботажем и шантажом.\\n• «Параграф 47»: переговоры с информатором, варианты мирного решения или передачи безопасности.\\n• «Кодекс Танаки»: обучающий квест по корпоративной этике с влиянием на доступ к контрактам.\\n• Требования: высокая репутация Arasaka, чистое прошлое, прохождение корпоративных квестов.\\n", "mechanics_links": ["mechanics/combat/combat-overview.yaml"], "assets": []}, {"id": "rewards-skills", "title": "Награды и характеристики", "body": "• Награды: кредиты, рост репутации в Arasaka, доступ к корпоративным операциям, уникальные предметы (например, «Кодекс Танаки», Compliance Badge, Legal Eagle Optics).\\n• Перки: «Чистые руки» (+10% к оплате за чистые операции), «Корпоративная этика» (расширенный пул контрактов).\\n• Навыки: Технические 6/10, Социальные 10/10, Боевые 2/10, Интеллект 10/10, Хладнокровие 10/10.\\n", "mechanics_links": [], "assets": []}, {"id": "arcs", "title": "Арки развития", "body": "• «Несгибаемая»: игрок укрепляет систему комплаенса, Юрико уходит легендой.\\n• «Трещина в системе»: обнаружение злоупотреблений приводит к внутреннему конфликту и выбору между правдой и лояльностью.\\n• «Падение идеала»: предательство игрока превращает её в параноидального тирана процедур.\\n• Эмоциональная траектория: уверенность → сомнение → триумф или цинизм к 2093 году.\\n", "mechanics_links": [], "assets": []}, {"id": "dev-notes", "title": "Заметки для разработчиков", "body": "• Визуальные референсы: японские деловые женщины старшего возраста, минимализм, акцент на глаза и импланты.\\n• Голос: женский, спокойный, с лёгким японским акцентом, медленный темп.\\n• Геймплей: сюжетно неуязвима, высокие требования к репутации, вызывает ответ Arasaka при агрессии.\\n• Связанные материалы: «Кодекс Танаки», отчёты комплаенса 2080–2093, сценарии Arasaka.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": ["Cyberpunk 2077", "Cyberpunk Red", {"Cyberpunk": "Edgerunners"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-13", "author": "concept_director", "changes": "Конвертация детализированного профиля Юрико Танаки из MD в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

