INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-arasaka-director-legal-2060-2077', 'NPC: Мика Окада — директор Legal & Compliance Arasaka (2060–2077)', '{"metadata": {"id": "npc-arasaka-director-legal-2060-2077", "title": "NPC: Мика Окада — директор Legal & Compliance Arasaka (2060–2077)", "document_type": "canon", "category": "npc-lore/corporate", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "arasaka", "legal"], "topics": ["corporate-law", "compliance"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "arasaka-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka/director-legal-2060-2077.md", "visibility": "internal", "audience": ["concept", "narrative", "quest"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Мики Окады хранился в Markdown без структурированной информации для сценаристов и системных команд.", "goal": "Описать директора Legal & Compliance Arasaka в формате знаний, сохранив юридические арки и игровые крючки.", "essence": "Опытная юристка управляет корпоративными судами и стандартами Arasaka, предлагая правовые ветки и compliance-механику.", "key_points": ["Руководит департаментом Legal & Compliance в 2060–2077 годах.", "Знаменита прецедентами против Militech и лоббированием NET-стандартов.", "Вовлекает игроков в юридические квесты, снижая штрафы и открывая уникальные перки."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и контекст", "body": "Мика Окада, прозвище «Шёлковая удавка», контролирует юридические операции Arasaka. Она сочетает судебные процессы,\\nарбитражи и compliance-политику, защищая корпорацию от претензий Militech и правительств. Её рабочая зона — этажи\\nArasaka Tower в Найт-Сити и штаб в Токио.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/world/world-governance.yaml"], "assets": []}, {"id": "appearance", "title": "Внешность и манера", "body": "Стройная женщина 65–70 лет, всегда одетая в безупречный корпоративный костюм. Держит идеальную осанку, сохраняет\\nхолодный взгляд и предпочитает минимальную кибернетику, подчёркивая статус интеллекта и компетенции.\\n", "mechanics_links": [], "assets": []}, {"id": "history", "title": "Биография и ключевые дела", "body": "Прошла путь от корпоративного юриста до директора департамента. Важные кейсы: прецедент ответственности поставщиков\\nкуполов (2062), громкое дело против Militech о саботаже (2069), лоббирование NET-стандартов безопасности (2074).\\nВ настоящее время курирует мета-суды и compliance-кампании.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и установки", "body": "Мика считает арбитраж и формулировки контрактов оружием корпорации. Её цель — закрепить стандарты, выгодные Arasaka,\\nи не допустить утечек. Боится политического отката и обожает идею корпоративной конституции.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и влияние", "body": "Полностью лояльна Arasaka, ведёт юридическую войну с Militech и использует NCPD/DAO как инструменты. Для игроков\\nслужит интерфейсом между корпоративным правом и уличной динамикой: доверие растёт через выполнение правовых поручений.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "NPC выдаёт квесты по compliance, снижению штрафов, правовым переговорам и доступу к юридическим перкам. Она\\nоткрывает дополнительные ветки при работе с корпоративными контрактами и урегулировании последствий нарушений.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "dialogue", "title": "Манера речи", "body": "Манера речи точная и контролирующая. Ключевая цитата: «Самое острое лезвие — это формулировка в контракте». Интонация\\nзависит от репутации: от холодной оценки до профессионального уважения.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"type": "internal", "link": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka-2020-2093.md"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован профиль Мики Окады в структурированное знание."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

