INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-citizen-city-blogger-2070s-city-center', 'Городской блогер (City Center, 2070-е)', '{"metadata": {"id": "npc-common-citizen-city-blogger-2070s-city-center", "title": "Городской блогер (City Center, 2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "influencer", "city-center"], "topics": ["social_media", "public_opinion"], "related_systems": ["narrative-service", "world-simulation"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/citizens/city-blogger-2070s-city-center.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо зафиксировать городского инфлюенсера, формирующего общественные настроения в City Center.", "goal": "Описать профиль блогера, его каналы влияния и применение в квестах и событиях.", "essence": "Блогер ведёт стримы, собирает слухи и за счёт подписчиков влияет на восприятие игроков и NPC.", "key_points": ["Активен в 2070-х годах, следит за горячими новостями района.", "Использует стримы и сетевые платформы для распространения информации.", "Может создавать волны общественного мнения, влияя на игровой мир."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Независимый блогер, обитающий в высотках City Center. Работает в одиночку, но имеет сеть информаторов среди курьеров и охранников.\\nОсновной инструмент — прямые эфиры и короткие клипы, разоблачающие коррупцию и рекламные кампании корпораций.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "influence", "title": "Каналы влияния", "body": "- Стримы и публикации, повышающие или снижающие уровень тревоги в районе.\\n- Сбор слухов о корпорациях и фиксация городских событий.\\n- Коллаборации с другими инфлюенсерами для усиления сообщений.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "interactions", "title": "Взаимодействия с игроком", "body": "- Предлагает делиться инсайдами за вознаграждение или связи.\\n- Может запустить волну поддержки или негативную кампанию против игрока.\\n- Предоставляет задания на поиск информации и медиаматериала.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "dialogue_sample", "title": "Образец реплики", "body": "«Подписывайся, если хочешь правду — или шоу. У меня они всегда идут в паре.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль городского блогера в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

