-- NPC: npc-arasaka-director-security-2045-2060
INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-arasaka-director-security-2045-2060', 'NPC: Наоко Кимура — директор корпоративной безопасности Arasaka (2045–2060)', '{"metadata": {"id": "npc-arasaka-director-security-2045-2060", "title": "NPC: Наоко Кимура — директор корпоративной безопасности Arasaka (2045–2060)", "document_type": "canon", "category": "npc-lore/corporate", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "arasaka", "security"], "topics": ["corporate-security", "counter-intelligence"], "related_systems": ["narrative-service", "quest-service"], "related_documents": [{"id": "arasaka-2020-2093", "relation": "references"}, {"id": "factions-overview", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka/director-security-2045-2060.md", "visibility": "internal", "audience": ["concept", "narrative", "quest"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль Наоко Кимуры существовал в Markdown без структурированных полей и описания игровых связей.", "goal": "Перенести сведения о директоре корпоративной безопасности Arasaka в схему знаний для синхронизации с нарративными и системными командами.", "essence": "Ветеран Arasaka курирует глобальную безопасность корпорации, демонстрируя высокий уровень дисциплины, имплантов и стратегического мышления.", "key_points": ["Руководит департаментом Corporate Security между 2045 и 2060 годами.", "Имеет офицерский набор боевых имплантов и командные центры в Найт-Сити и Токио.", "Обеспечивает игроку доступ к квестам по защите активов и протоколам безопасности."]}, "content": {"sections": [{"id": "profile", "title": "Профиль и контекст", "body": "Наоко Кимура, известная как «Страж», возглавляет корпоративную безопасность Arasaka в период 2045–2060 годов. Она\\nстроит многоуровневые протоколы защиты активов, перекрывает угрозы со стороны банд и конкурирующих корпораций и\\nуправляет сетью командных центров между Найт-Сити и Токио. Карьерный путь включает службу телохранителем и начальником\\nохраны объектов, что сформировало практичный подход к управлению рисками.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/world/world-governance.yaml"], "assets": []}, {"id": "appearance", "title": "Внешность и импланты", "body": "Подтянутая женщина 57 лет с короткой чёрной стрижкой и множеством боевых имплантов: профессиональная оптика Kiroshi,\\nофицерский Sandevistan, усиленные сухожилия, поддермальная броня и Smart Link. Предпочитает тактическую корпоративную\\nформу чёрно-серой гаммы с красными акцентами и скрытыми бронированными элементами.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/combat/combat-implants-system.yaml"], "assets": []}, {"id": "history", "title": "Биография и ключевые события", "body": "Выпускница военной академии и корпоративных курсов безопасности, Наоко прошла путь от телохранителя до директора\\nArasaka Corporate Security. Вехи: предотвращение покушения на VIP (2046), реформирование купольных протоколов (2051),\\nконтр-операция против Animals и Maelstrom совместно с NCPD (2056). В текущем цикле она обучает преемников и следит за\\nглобальной инфраструктурой Arasaka.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Основная мотивация — абсолютная безопасность активов Arasaka. Ключевые цели включают стандартизацию протоколов,\\nснижение рисков и усиление учений. Она опасается системных утечек и координированных атак, мечтая создать идеальную\\nмногоуровневую систему защиты.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и отношения", "body": "Полностью предана Arasaka, воспринимает Militech как конкурента, сотрудничает с NCPD и Trauma Team по контрактам.\\nОтношение к бандам — враждебное; от игроков ожидает дисциплины и соблюдения протоколов. Лояльность повышается через\\nуспешные операции по защите объектов.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "NPC предоставляет квесты охраны, эскорта и контр-интеллигентных операций, открывает доступ к security-перкам и\\nспециализированному снаряжению. Высокие боевые навыки и корпоративные связи делают её ключевой фигурой в арках,\\nсвязанных с защитой Arasaka.\\n", "mechanics_links": ["shared/docs/knowledge/mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "dialogue", "title": "Манера речи", "body": "Кимура говорит сухо и по процедурам. Её основные реплики подчёркивают дисциплину: «Протоколы написаны кровью. Следуем\\nим — остаёмся в живых». Взаимодействие меняется в зависимости от репутации: от формальной оценки до признания\\nнадёжности.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"type": "internal", "link": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/arasaka-2020-2093.md"}, {"type": "internal", "link": "shared/docs/knowledge/canon/lore/_03-lore/factions/factions-overview.yaml"}], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_team", "changes": "Конвертирован профиль Наоко Кимуры в структурированное знание."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

