INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-biotechnica-vp-agro-rnd-2060-2080', 'VP Agro R&D Biotechnica (2060-2080)', '{"metadata": {"id": "npc-biotechnica-vp-agro-rnd-2060-2080", "title": "VP Agro R&D Biotechnica (2060-2080)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "biotechnica", "agro-tech"], "topics": ["synthetic-crops", "biofuel"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-corporations-biotechnica-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/biotechnica/vp-agro-rnd-2060-2080.md", "visibility": "internal", "audience": ["concept", "narrative", "engineering"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль вице-президента Biotechnica хранился в Markdown и не участвовал в сюжетах агрологистики и биотоплива.", "goal": "Стандартизировать образ доктора Алессии Феррацци для миссий развития синтетических культур и экономических альянсов.", "essence": "Учёный-управленец создаёт Gen-4 культуры и биотопливо, обеспечивая автономию куполов и ведя переговоры с PetroChem.", "key_points": ["Руководит аграрными R&D программами Biotechnica.", "Строит союзы с PetroChem для биотоплива и купольных инфраструктур.", "Опасается саботажа и краж штаммов; стремится к стандартам Gen-5."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Доктор Алессия Феррацци — вице-президент по агро-R&D, ведущая работу в Римини и Night City. Использует Bio-lab\\nSuite и Neural Link для управления лабораториями и контролирующими системами куполов.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "innovations", "title": "Инновации", "body": "Курирует разработки синтетических культур Gen-4, повышает устойчивость растений к техногенным условиям куполов,\\nзаключает партнёрства с PetroChem по биотопливу.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится обеспечить продовольственную автономию и выпуск биотоплива с КПД более 40%. Ждёт политического\\nпризнания агро-протоколов Biotechnica. Боится утечек штаммов и корпоративного шпионажа.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт игроку квесты по защите биостанций, доставке штаммов и переговорам с корпорациями. Навыки: технические —\\nочень высокие, социальные — средние, боевые — низкие. Предоставляет био-баффы и улучшения для куполов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Саботаж PetroChem или конкурентов.\\n- Вывод Gen-5 культур на рынок и борьба за патенты.\\n- Переговоры с купольными администрациями об автономии.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль VP Agro R&D Biotechnica в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

