INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-kangtao-vp-export-2055-2075', 'VP Export Operations Kang Tao (2055-2075)', '{"metadata": {"id": "npc-kangtao-vp-export-2055-2075", "title": "VP Export Operations Kang Tao (2055-2075)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "kang-tao", "trade"], "topics": ["export-operations", "corporate-diplomacy"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-corporations-kang-tao-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/kang-tao/vp-export-operations-2055-2075.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль вице-президента по экспортным операциям Kang Tao был в Markdown и не поддерживал сюжеты торговой экспансии.", "goal": "Структурировать данные о Вэе Чжэньлуне для квестов дипломатии, контрактов и конкуренции с Militech.", "essence": "Харизматичный переговорщик открывает глобальные рынки, обходя конкурентов и политики с помощью торговых сделок.", "key_points": ["Заключает крупные контракты на поставку смарт-оружия, включая сделку с NUSA.", "Использует дипломатические сети и фиксёров для серых операций.", "Ведёт торговые войны против Militech и Arasaka, сохраняя имидж «мирной экспансии»."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Вэй Чжэньлун — вице-президент по экспортным операциям Kang Tao и член совета директоров. Работающий из штабов в\\nПекине и Найт-Сити, он управляет глобальной сетью переговоров и поставок, используя элитные костюмы и минималистичную\\nкибернетику для усиления харизмы.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "biography", "title": "Карьера и достижения", "body": "С 2010 года поднимается по корпоративной лестнице, открывает рынки в 47 странах и заключает контракты на сотни\\nмиллиардов евро. В 2065 году оформляет знаменитую сделку с NUSA, обойдя Militech и укрепив позиции Kang Tao.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и философия", "body": "Считает торговлю движущей силой мира и ставит цели по доминированию Kang Tao без прямых войн. Опасается разрыва\\nдипломатических каналов и политических кризисов. Ценит терпение и долгосрочные планы.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и конкуренции", "body": "Militech — главный антагонист на экспортном рынке, Arasaka — осторожный партнёр в Азии, фиксёры — посредники для\\nсерых сделок, правительства — ключевые точки влияния. Вэй использует харизму и данные, чтобы склонить их на свою сторону.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт игроку задачи по сопровождению переговоров, защите грузов и выявлению шпионов конкурентов. Навыки: социальные —\\nочень высокие, технические — средние, боевые — низкие. Успех квестов открывает уникальные скидки и технологии Kang Tao.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml", "mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- «Шёлковый путь» — сопровождение секретных переговоров в Африке.\\n- «Контракт с дьяволом» — демонстрация смарт-оружия в опасной зоне.\\n- Раскрытие корпоративного саботажа Militech или утечек данных внутри Kang Tao.\\n", "mechanics_links": ["mechanics/economy/economy-trading-guilds.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль VP Export Operations Kang Tao в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

