INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-militech-head-rnd-2035-2050', 'Глава R&D Militech (2035-2050)', '{"metadata": {"id": "npc-militech-head-rnd-2035-2050", "title": "Глава R&D Militech (2035-2050)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "militech", "rnd"], "topics": ["weapons-research", "corporate-competition"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-corporations-militech-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/militech/head-rnd-2035-2050.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль руководителя R&D Militech находился в Markdown и не был доступен для сценариев прототипов.", "goal": "Структурировать данные об Elias Rowe для сюжетов гонки вооружений между корпорациями.", "essence": "Учёный-корпорат ведёт линейки Militech Gen-2, создаёт прототипы и вовлекает игроков в тесты вооружений.", "key_points": ["Руководит разработкой смарт-платформ, экзо-бронекостюмов и анти-дроновых систем.", "Сталкивается с конкуренцией Arasaka и Kang Tao.", "Предоставляет доступ к экспериментальному луту за выполнение тестовых миссий."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Доктор Elias Rowe — академик, ставший корпоративным руководителем R&D Militech. Работает на полигонах Остина и\\nНайт-Сити, координирует лаборатории и испытательные группы.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "projects", "title": "Ключевые проекты", "body": "- 2037: Смарт-платформы для штурмовых групп.\\n- 2042: Энергосистемы экзо-бронекостюмов.\\n- 2048: Анти-дроновые комплексы.\\nПродолжает ускоренную гонку вооружений против конкурентов.\\n", "mechanics_links": ["mechanics/combat/combat-weapon-classes.yaml"], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится к технологическому превосходству Militech, боится провала полигонных тестов и сокращения финансирования.\\nЖелаемая цель — выпуск чёрных прототипов второго поколения.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Корпоративные связи", "body": "Конкурирует с Arasaka и Kang Tao, сотрудничает с NUSA и частично с NetWatch, держит особый контроль над внутренними\\nутечками.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Выдаёт цепочки прототипов и тестовых миссий, открывает экспериментальный лут и модернизации. Навыки: тех —\\nочень высокие, социальные — средние, боевые — низкие.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Испытания новых линек Militech Gen-2.\\n- Корпоративное шпионаж против конкурентных лабораторий.\\n- Выбор: поддержать Rowe или слить данные конкуренции.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль главы R&D Militech в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

