INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-trauma-regional-director-2070-2085', 'Региональный директор Trauma Team (2070-2085)', '{"metadata": {"id": "npc-trauma-regional-director-2070-2085", "title": "Региональный директор Trauma Team (2070-2085)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "trauma-team", "operations"], "topics": ["emergency-logistics", "sla-management"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-corporations-trauma-team-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/trauma-team/regional-director-2070-2085.md", "visibility": "internal", "audience": ["concept", "narrative", "operations"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль регионального директора Trauma Team находился в Markdown и не задействовал процессы SLA и эвакуаций.", "goal": "Структурировать данные о докторе Эйдене Чане для квестов логистики, политических договоров и реагирования на катастрофы.", "essence": "Руководитель региона превращает спасение жизней в управляемый сервис, поддерживая SLA 99,9% в Найт-Сити.", "key_points": ["Сократил время реагирования на 18% и ввёл «чёрные коридоры» для горячих зон.", "Строит политическую поддержку привилегий Trauma Team.", "Боится массовых катастроф, которые могут сорвать логистику."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Доктор Эйден Чан руководит региональными операциями Trauma Team из штаб-квартиры Найт-Сити. Мед-корпо стиль и\\nимпланты MedSuite Pro подчёркивают деловой подход к спасению жизней.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "operations", "title": "Операции и реформы", "body": "Оптимизировал базы данных вызовов, внедрил «чёрные коридоры» через опасные районы и сократил время реагирования\\nна 18%. Настраивает взаимодействие с авиабазами и частными партнёрами.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Видит спасение как сервис, который должен работать без сбоев. Хочет обеспечить SLA 99,9% в Найт-Сити и расширить\\nсеть на прилегающие регионы, опасаясь блокировок и катастроф.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и политика", "body": "Поддерживает контракты с Arasaka и Militech, координирует действия с NCPD и рассматривает банды как угрозы,\\nспособные сорвать логистику.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Выдаёт квесты по организации эвакуаций, защите коридоров и переговорам с властями. Навыки: медицинские и\\nлогистические — высокие, социальные — средние, боевые — низкие.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Координация ответов на массовые катастрофы.\\n- Лоббирование расширенных привилегий для Trauma Team.\\n- Дилемма между прибыльностью и гуманностью сервиса.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль регионального директора Trauma Team в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

