INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-netwatch-regional-director-2055-2070', 'Региональный директор NetWatch (2055-2070)', '{"metadata": {"id": "npc-netwatch-regional-director-2055-2070", "title": "Региональный директор NetWatch (2055-2070)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "netwatch", "net-security"], "topics": ["legal-enforcement", "net-operations"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-corporations-netwatch-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/netwatch/regional-director-2055-2070.md", "visibility": "internal", "audience": ["concept", "narrative", "security"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль регионального директора NetWatch был в Markdown и не использовался в сюжетах зачисток сети.", "goal": "Структурировать данные о Викторе Клайне для миссий юридического надзора и операций против нелегальных узлов.", "essence": "Хранитель сетевого закона координирует зачистки NetWatch и добивается поддержки правительства для расширения полномочий.", "key_points": ["Руководит региональными операциями NetWatch по зачисткам и надзору.", "Балансирует между сотрудничеством с корпорациями и борьбой с Voodoo Boys.", "Строит политические альянсы для укрепления правового контроля в NET."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Виктор Клайн — сдержанный корпоративный управленец. Использует Secure Neural Link и ICE Hardened для\\nкоординации операций, избегает ярких имплантов и предпочитает документооборот, подкреплённый юридическими\\nпротоколами NetWatch.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "operations", "title": "Операции и достижения", "body": "Прошёл путь от аналитика угроз до руководителя региона. Организовал операции против Voodoo Boys, перекрыл\\nнесколько чёрных ICE-рынков, разработал стандарты зачисток узлов в Night City и прилегающих куполах.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Верит в порядок и закон в NET. Пытается стандартизировать протоколы зачисток и добиться политической поддержки\\nполномочий NetWatch. Опасается внутренних утечек и коррупции.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт игроку квесты по зачистке нелегальных узлов, снятию сетевых штрафов и защите инфраструктур. Навыки:\\nхакерские — высокие, социальные — средние, боевые — низкие. Предоставляет доступ к юридическим инструментам и\\nсетевым операциям.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Расследование утечек внутри NetWatch.\\n- Политическая борьба за расширение полномочий.\\n- Совместные операции с Arasaka/Militech против нелегальных сетей.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль регионального директора NetWatch в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

