INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-trauma-chief-pilot-2068-2088', 'Chief Pilot Trauma Team (2068-2088)', '{"metadata": {"id": "npc-trauma-chief-pilot-2068-2088", "title": "Chief Pilot Trauma Team (2068-2088)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "trauma-team", "pilot"], "topics": ["emergency-response", "aviation-operations"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-corporations-trauma-team-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/corporations/trauma-team/chief-pilot-2068-2088.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Детализированный профиль главного пилота Trauma Team находился в Markdown и не использовался в сюжетах авиаподдержки.", "goal": "Перенести данные об Изабель «Иззи» Ортеге в структурированную базу для миссий элитных пилотов и обучения новичков.", "essence": "Легендарный пилот возвращается в небо после катастрофы, создавая школу «Ангелов неба» и ведя самые опасные операции.", "key_points": ["Ветеран ВВС NUSA, перешедшая в Trauma Team ради спасения жизней.", "Пережила катастрофу 2065, получила кибернетические импланты и вернулась в строй.", "Обучает элитных пилотов и лично ведёт критические вылеты."]}, "content": {"sections": [{"id": "career", "title": "Карьера и достижения", "body": "Иззи прошла путь от военного пилота до главного пилота Trauma Team. После службы на транспортниках и эвакуационных\\nмиссиях NUSA возглавила программу «Ангелы неба», снизив смертность пилотов на 40%.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "trauma_event", "title": "Катастрофа 2065 и возвращение", "body": "Во время конфликта Militech и Arasaka её AV был подбит. Несмотря на тяжёлые травмы и потерю конечности, она\\nпосадила горящую машину и спасла команду. Три года реабилитации завершились возвращением в небо и назначением\\nна должность главного пилота.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и страхи", "body": "Верит в долг пилота забрать клиента живым любой ценой. Боится превратить Trauma Team в бездушный бизнес и\\nопасается, что возраст когда-нибудь помешает спасению жизней.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт игроку миссии «Код красный», «Падший ангел» и «Последний полёт». Навыки: пилотирование — экстремальные,\\nлидерство — высокое, боевые — средние. Может сопровождать игрока как NPC в воздушных операциях.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Обучение игрока пилотированию AV в условиях боевого хаоса.\\n- Спасение ученика, захваченного Wraiths.\\n- Дилемма между героической смертью или передачей эстафеты новому поколению.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль главного пилота Trauma Team в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

