INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-common-citizen-corporate-office-worker-2070s', 'Корпоративный клерк (2070-е)', '{"metadata": {"id": "npc-common-citizen-corporate-office-worker-2070s", "title": "Корпоративный клерк (2070-е)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "corporate", "city-center"], "topics": ["corporate_life", "background_characters"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [], "source": "shared/docs/knowledge/canon/narrative/npc-lore/common/citizens/corporate-office-worker-2070s.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Требуется описать типового корпоративного клерка для фоновых сцен и микро-квестов City Center.", "goal": "Стандартизировать профиль офисного NPC, обеспечивающего доступ к корпоративным слухам и поручениям.", "essence": "Занятой сотрудник корпорации, балансирующий отчёты, стендапы и внутренние интриги, готовший делиться информацией за помощь.", "key_points": ["Постоянно торопится между встречами и требует быстрых взаимодействий.", "Знает о внутренних корпоративных конфликтах и может выдавать слухи.", "Поддерживает атмосферу повседневной корпоративной рутины."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Сотрудник среднего звена, закреплённый за юридическим или финансовым отделом. Часто работает сверхурочно,\\nзависим от корпоративных сервисов доставки и VR-стендапов.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "location", "title": "Локация", "body": "Расположен в офисных кварталах City Center, чаще у входов в небоскрёбы корпораций или в бизнес-лаунджах.\\nМожно встретить возле лифтов, офисных кухонь и терминалов отчётности.\\n", "mechanics_links": [], "assets": []}, {"id": "interactions", "title": "Взаимодействия", "body": "- Просит помочь с передачей документов или устранением сбоев в корпоративных системах.\\n- Делится слухами о конкурентах, если игрок сокращает его рабочую нагрузку.\\n- Может выдать временный пропуск или чистую запись для системы доступа.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "dialogue_sample", "title": "Образец реплики", "body": "«Извините, у меня стендап через пять минут. Если ты по делу — говори быстро.»\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль корпоративного клерка в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

