INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-6th-commander-2060-2070', 'Командир 6th Street (2060-2070)', '{"metadata": {"id": "npc-6th-commander-2060-2070", "title": "Командир 6th Street (2060-2070)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "6th-street", "santo-domingo"], "topics": ["vigilante", "community-defense"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/6th-street/commander-2060-2070.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Командир 6th Street был описан в Markdown и не участвовал в сценариях городских дружин.", "goal": "Структурировать профиль лидера для сюжетов патрулей и охраны района.", "essence": "Ветеран Richard Kelly организует дружины 6th Street, навязывая порядок Santo Domingo силой и договорами с жителями.", "key_points": ["Продвигает идею «народной милиции» и контролирует патрули.", "Ведёт конфликты с Valentinos и напряжённые переговоры с NCPD.", "Предоставляет игроку задания по обороне и сопровождению."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Richard Kelly — ветеран и лидер дружин 6th Street. Базируется в Santo Domingo, координирует патрули и внедряет\\nполувоенные стандарты дисциплины среди гражданских.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "history", "title": "История и идеология", "body": "После разочарования в NCPD создал «народную милицию». Организовал патрули, защитил кварталы от вымогателей, но\\nприбегает к жёстким методам, что вызывает критику жителей.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится удержать порядок любой ценой, боится потерять доверие общины и мечтает легализовать 6th Street как\\nохранную компанию. Опасается, что банда скатится в криминал.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи", "body": "Valentinos — постоянные враги с перемириями, Arasaka/Militech — прагматичные сделки, NCPD — напряжённые отношения,\\nжители Santo Domingo — объект протекторатного контроля.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Дает квесты патрулей, обороны, эскорта и перемирий. Навыки: боевые — высокие, социальные — средние, технические —\\nнизкие. Предоставляет доступ к скидкам на оружие и поддержку в боевых событиях.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Укрепление блокпостов против рейдов.\\n- Договоры о перемирии с другими бандами.\\n- Внутренние споры о методах 6th Street.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль командира 6th Street в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

