INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-6th-civil-defense-2066-2078', 'Координатор гражданской обороны 6th Street (2066-2078)', '{"metadata": {"id": "npc-6th-civil-defense-2066-2078", "title": "Координатор гражданской обороны 6th Street (2066-2078)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "6th-street", "santo-domingo"], "topics": ["community-defense", "militia-coordination"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/6th-street/civil-defense-coordinator-2066-2078.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание координатора гражданской обороны 6th Street существовало в Markdown и не было интегрировано в игровую базу знаний.", "goal": "Формализовать профиль Сэма Макрея для сценариев обороны кварталов и взаимодействия с жителями Santo Domingo.", "essence": "Ветеран NUSA превращает 6th Street в «народную милицию», организуя оборонительные сети и удерживая доверие кварталов.", "key_points": ["Создаёт систему гражданской обороны и укрытий в Santo Domingo.", "Руководит обороной кварталов против Maelstrom и других угроз.", "Ведёт переговоры с жителями, поддерживая образ «защитников»."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Сэм «Капитан» Макрей — бывший офицер NUSA, вернувшийся в Santo Domingo и возглавивший гражданскую оборону 6th\\nStreet. Он строит сеть опорных пунктов, безопасных маршрутов и тренировочных программ для жителей, позиционируя\\nдружину как защитников, а не бандитов.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "history", "title": "История и наследие", "body": "После Четвёртой корпоративной войны Макрей основал командный центр обороны, координировал патрули и создал укрытия.\\nВ 2070 году организовал легендарную оборону против рейда Maelstrom: 200 бойцов 6th Street защитили квартал без\\nжертв среди гражданских.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Хочет превратить 6th Street из банды в легитимную милицию. Боится, что радикалы дискредитируют движение. Цели —\\nбезопасность семей, укрепление общественных связей и признание NCPD.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и политика", "body": "Поддерживает доверие жителей Santo Domingo, жёстко противостоит Maelstrom и Wraiths, критично относится к NCPD,\\nкоторые не защищают район. Имеет осторожные сделки с фиксёрами для снабжения.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт игроку квесты по подготовке обороны, эвакуации гражданских и сбору данных о рейдах. Навыки: боевые —\\nвысокие, лидерские — высокие, дипломатические — средние. Повышение репутации открывает оборонительные бонусы.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Подготовка баррикад и обучение ополченцев.\\n- Сбор компромата на коррумпированных офицеров NCPD.\\n- Дилемма между жёстким режимом и доверием населения.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль координатора гражданской обороны в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

