INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-6th-quartermaster-2062-2072', 'Квартирмейстер 6th Street (2062-2072)', '{"metadata": {"id": "npc-6th-quartermaster-2062-2072", "title": "Квартирмейстер 6th Street (2062-2072)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "6th-street", "logistics"], "topics": ["supply-chains", "militia-economy"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/6th-street/quartermaster-2062-2072.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль квартирмейстера 6th Street хранился в Markdown и не поддерживал сценарии снабжения дружин.", "goal": "Стандартизировать данные о Мэри «Серж» Гриффин для квестов логистики, конвоев и экономического баланса квартала.", "essence": "Ветеран-логист держит на плаву запасы 6th Street, управляя складскими узлами, сделками и защитой караванов.", "key_points": ["Обеспечивает запас прочности на 90 дней и управляет складскими маршрутами.", "Ведёт сделки с Valentinos и Militech ради снабжения.", "Запускает миссии по защите конвоев и борьбе с саботажем."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Мэри «Серж» Гриффин — бывший квартирмейстер армии, отвечающая за снабжение дружин 6th Street. Её главный штаб\\nрасположен на складских узлах Santo Domingo, где она распределяет вооружение, топливо и медикаменты.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "logistics_network", "title": "Логистическая сеть", "body": "В период топливных кризисов стабилизировала поставки, внедрила учёт складов и восстановила каналы обмена с\\nфиксёрами. Координирует безопасные маршруты и контролирует инвентаризацию.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Одержима порядком и эффективностью. Боится краж и саботажа. Цель — заключить легальные контракты и обеспечить\\nавтономное снабжение дружин.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и сделки", "body": "Сотрудничает с Valentinos по обмену ресурсами, закупает через Militech избытки вооружений, избегает Tyger Claws,\\nподдерживает редкие контакты с NCPD для формальной отчётности.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт квесты на охрану караванов, поиск запасов, восстановление складов. Навыки: технические и логистические —\\nвысокие, боевые — средние, социальные — средние. Предоставляет скидки на боеприпасы и расходники.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Защита складов от диверсий.\\n- Контракты на серые поставки через фиксёров.\\n- Решения о распределении дефицитных ресурсов между кварталами.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль квартирмейстера 6th Street в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

