INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-animals-alpha-2070-2085', 'Alpha — лидер Animals (2070-2085)', '{"metadata": {"id": "npc-animals-alpha-2070-2085", "title": "Alpha — лидер Animals (2070-2085)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "animals", "combat-sports"], "topics": ["underground-tournaments", "booster-chemistry"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/animals/alpha-2070-2085.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль лидера Animals оставался в Markdown и не включался в сценарии нелегальных рингов и усилителей.", "goal": "Структурировать образ Alpha для квестов культовой силы, турниров и конфликтов с конкурирующими бандами.", "essence": "Харизматичный промоутер превращает культ силы Animals в прибыльный спорт, продвигая нелегальные арены и допинги.", "key_points": ["Руководит сетью тренировочных залов и подпольных турниров.", "Экспериментирует с эксклюзивными допингами и усилителями.", "Борется за признание Animals как «спорта», удерживая баланс с Maelstrom и NCPD."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Alpha — необъявленный лидер Animals, известный массой и боевыми тату. Боевые импланты Gorilla Arms Max, Berserk\\nMk.5 и титановая броня делают его живой рекламой культа силы.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "operations", "title": "Операции и бизнес", "body": "Управляет нелегальными аренами в Pacifica и Watson, ведёт переговоры о «защите» площадок, конкурирует с\\nMaelstrom и 6th Street за контроль аудитории.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и страхи", "body": "Культ силы и признание Animals как законного спорта. Боится запретов на импланты и утечки эксклюзивных формул\\nдопинга.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает турниры, испытания на аренах и сделки по усилителям. Навыки: боевые — экстремальные, социальные —\\nнизкие, технические — низкие. Игрок может получить уникальные усилители и доступ к Animals Gym.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Допинговый скандал, угрожающий Animals.\\n- Союзы или конфликты с Voodoo Boys и Maelstrom за контроль арен.\\n- Рейды NCPD и необходимость спрятать лаборатории усилителей.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль лидера Animals в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

