INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-maelstrom-logistics-2068-2080', 'Офицер по логистике Maelstrom (2068-2080)', '{"metadata": {"id": "npc-maelstrom-logistics-2068-2080", "title": "Офицер по логистике Maelstrom (2068-2080)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "maelstrom", "logistics"], "topics": ["black-market", "cyberware-salvage"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/maelstrom/logistics-officer-2068-2080.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сведения об офицере логистики Maelstrom находились в Markdown и не поддерживали сценарии чёрного рынка кибервара.", "goal": "Структурировать профиль Клэя для квестов снабжения, рейдов и серых сделок с имплантами.", "essence": "Техно-фанатичный логист превращает трофейные импланты в ресурс Maelstrom, удерживая сеть обмена и сборочных линий.", "key_points": ["Оптимизировал разборку и сборку трофейных имплантов в Watson.", "Налаживает каналы с фиксёрами, Animals и Tyger Claws.", "Запускает миссии по поставкам, рейдам и защите сборочных линий."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Клэй руководит склад-бункерами Maelstrom в Watson, контролируя поток киберваря и деталей. Его агрессивный\\nтех-панк стиль отражает одержимость модификациями и сборкой.\\n", "mechanics_links": ["mechanics/economy/economy-logistics.yaml"], "assets": []}, {"id": "operations", "title": "Операции и каналы", "body": "Организует разборку трофейных имплантов, создаёт временные сборочные линии и поддерживает обмен с фиксёрами.\\nИспользует подпольные маршруты для доставки деталей на чёрный рынок.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится к постоянному потоку деталей и мечтает о собственной сборочной линии. Боится засад Trauma Team и NCPD,\\nохотится за эксклюзивами чёрных лабораторий.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи", "body": "Работает с Mr. Hands по сделкам, поддерживает нейтралитет с Animals, изредка обменяется товарами с Tyger Claws.\\nНенавидит вмешательство корпораций и силовиков.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт квесты на поставку кибервара, рейды на склады конкурентов и защиту караванов. Навыки: технические — высокие,\\nбоевые — средние, социальные — низкие. Предоставляет доступ к мод-комплектам Maelstrom.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Создание подпольной линии апгрейдов.\\n- Срыв поставок Trauma Team или NCPD.\\n- Баланс между жадностью Maelstrom и защитой собственного клана.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль офицера по логистике Maelstrom в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

