INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-mox-evac-coordinator-2068-2082', 'Координатор эвакуаций Mox (2068-2082)', '{"metadata": {"id": "npc-mox-evac-coordinator-2068-2082", "title": "Координатор эвакуаций Mox (2068-2082)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "mox", "watson"], "topics": ["evacuation-planning", "community-safety"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/mox/evac-coordinator-2068-2082.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль координатора эвакуаций Mox был доступен только в Markdown и не использовался в сюжетах защиты клубов.", "goal": "Структурировать данные об Эше Харпере для квестов эвакуации, медицинской логистики и взаимодействия с корпорациями.", "essence": "Практичный координатор строит сеть спасения для клубов Mox, договариваясь с Trauma Team и удерживая контроль над маршрутами.", "key_points": ["Создал «тихие» коридоры эвакуации и обучил персонал быстрому реагированию.", "Балансирует отношения с Tyger Claws, Trauma Team и NCPD ради безопасности общины.", "Предоставляет игроку миссии по сопровождению, защите и медицинской помощи."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Эш Харпер отвечает за эвакуацию и укрытие людей Mox. Использует сеть клубов в Watson и Westbrook как безопасные\\nточки и подключает партнеров для медицинской поддержки.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "history", "title": "История и достижения", "body": "Организовал за четыре минуты стандарт реагирования Mox, договорился с Trauma Team о приоритетных маршрутах и\\nпровёл учения по эвакуации после рейдов Tyger Claws.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивации и цели", "body": "Считает, что выживание — главная победа. Хочет создать городскую программу защиты работников индустрии\\nразвлечений и боится перекрытий маршрутов со стороны властей или банд.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и партнёры", "body": "- Tyger Claws — напряжённые переговоры по зонам влияния.\\n- Trauma Team — ключевой партнёр в экстренной медицине.\\n- NCPD — сложные отношения из-за бюрократии и коррупции.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Даёт квесты по эвакуации, сопровождению и защите свидетелей. Навыки: социальные и медицинские — высокие, боевые —\\nнизкие (делегирует охрану). Предоставляет скидки на мед-услуги и поддержку Mox.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Эвакуация клуба во время рейда Tyger Claws.\\n- Перевозка раненных через перекрытый район.\\n- Решение: раскрыть маршруты полиции или скрыть их ради безопасности Mox.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль координатора эвакуаций Mox в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

