INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-mox-security-chief-2068-2088', 'Начальник безопасности Mox (2068-2088)', '{"metadata": {"id": "npc-mox-security-chief-2068-2088", "title": "Начальник безопасности Mox (2068-2088)", "document_type": "canon", "category": "npc-lore", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "mox", "security"], "topics": ["club-protection", "anti-trafficking"], "related_systems": ["narrative-service", "combat-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/mox/security-chief-2068-2088.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль начальника безопасности Mox существовал в Markdown и не участвовал в сценариях защиты клубов.", "goal": "Формализовать образ Зары «Щит» Новак для квестов охраны, спасения и ответных ударов против Tyger Claws.", "essence": "Бывший корпосек превратила Mox в крепость, защищая работников индустрии без компромиссов.", "key_points": ["Создала систему безопасности после ухода из Arasaka.", "Остановила нападение Tyger Claws на Lizzie''s Bar в 2075.", "Запускает миссии по спасению похищенных и отражению атак."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Зара «Щит» Новак руководит безопасностью Mox из Lizzie''s Bar. Её прошлое в Arasaka сделало её безжалостным\\nзащитником, готовым убить любого, кто угрожает подопечным.\\n", "mechanics_links": ["mechanics/combat/combat-pvp-pve.yaml"], "assets": []}, {"id": "history", "title": "История и ключевые события", "body": "После отказа выполнить приказ Arasaka о зачистке борделя Зара помогла жертвам бежать и присоединилась к Mox.\\nВ 2075 году в одиночку отбила нападение Tyger Claws, закрепив репутацию непоколебимого защитника.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и характер", "body": "Жёсткая, но заботливая, Зара не терпит насилия над работниками индустрии. Её миссия — защищать «семью» Mox, даже\\nценой собственной жизни.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает игроку квесты по спасению похищенных, охране клубов и мести за нападения. Навыки: боевые — высокие,\\nтактические — высокие, социальные — средние.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- «Щит не падёт» — спасение похищенной работницы под таймер.\\n- Переговоры с Tyger Claws о прекращении атак или эскалация конфликта.\\n- Дилемма между местью и дипломатией для удержания контроля над районами.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль начальника безопасности Mox в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

