INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-tyger-consigliere-2062-2078', 'Consigliere Tyger Claws (2062-2078)', '{"metadata": {"id": "npc-tyger-consigliere-2062-2078", "title": "Consigliere Tyger Claws (2062-2078)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "tyger-claws", "diplomacy"], "topics": ["gang-diplomacy", "gray-economy"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/tyger-claws/consigliere-2062-2078.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Consigliere Tyger Claws был описан в Markdown и не использовался в сюжетах серых сделок и политических интриг.", "goal": "Стандартизировать образ Рё Кадзуо для сценариев легализации бизнеса и дипломатии между бандами и корпорациями.", "essence": "Элегантный стратег ведёт Tyger Claws через переговоры, сделки и «серые» схемы, стремясь к статусной неприкасаемости.", "key_points": ["Курирует связи с корпорациями, услугами и чиновниками Japantown.", "Стремится легализовать часть бизнеса, избегая публичных скандалов.", "Выстраивает отношения с Mox, NCPD и Arasaka."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Рё Кадзуо — consigliere Tyger Claws, сочетающий корпо-элегантность и символику якудза. Его импланты усиливают\\nпереговорные способности и личную безопасность.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "operations", "title": "Операции и влияние", "body": "Управляет инвестициями, «серым» бизнесом и политическими связями Japantown. Создаёт баланс между уличным кодексом\\nи корпоративными интересами.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится к устойчивости клана и легализации ключевых бизнесов. Боится публичного скандала, который разрушит\\nимидж Tyger Claws и его неприкасаемость.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает дипломатические квесты, сделки и «серые» услуги. Навыки: социальные — высокие, технические — низкие,\\nбоевые — низкие. Игрок может стать посредником между Tyger Claws и другими фракциями.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Легализация клубов и выход на «белый» рынок.\\n- Скрытая война с конкурирующими советниками или корпорациями.\\n- Дилеммы между интересами клана и собственными амбициями.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль consigliere Tyger Claws в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

