INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-valentinos-jefe-2060-2070', 'Хосе «El Jefe» Рамирес — лидер Valentinos (2060–2070)', '{"metadata": {"id": "npc-valentinos-jefe-2060-2070", "title": "Хосе «El Jefe» Рамирес — лидер Valentinos (2060–2070)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "valentinos", "gang-leader"], "topics": ["gang_politics", "npc_profiles"], "related_systems": ["narrative-service", "social-service"], "related_documents": [{"id": "district-heywood-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/valentinos/jefe-valentinos-2060-2070.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Необходимо структурировать данные о лидере Valentinos для работы с квестами и социальными системами Heywood.", "goal": "Зафиксировать образ El Jefe, его ценности и влияние на сообщество и кросс-фракционные дипломатии.", "essence": "Хосе «El Jefe» Рамирес — харизматичный и принципиальный лидер Valentinos, защищающий Heywood и поддерживающий латиноамериканскую культуру.", "key_points": ["Построил карьеру внутри Valentinos от Aspirante до Jefe, сохранив честь и традиции.", "Сосредоточен на защите сообщества Heywood и дипломатии с другими фракциями.", "Использует умеренный кибервар и культурные символы для укрепления идентичности банды."]}, "content": {"sections": [{"id": "profile", "title": "Основные сведения", "body": "Хосе «El Tigre» Рамирес, известный как El Jefe, возглавляет Valentinos в 2060–2070 годах. Родился в Heywood, женат, имеет троих детей. Категория important, статус — высокий приоритет для нарративных линий банды.\\n", "mechanics_links": [], "assets": []}, {"id": "appearance", "title": "Внешность и кибервар", "body": "Атлетическое телосложение, ухоженный вид, татуировки Valentinos. Использует Neural Link, Kiroshi Optics, лёгкую субдермальную броню, Reflex Tuner и Gorilla Arms средней мощности. Предпочитает стильные красно-золотые наряды с традиционными элементами.\\n", "mechanics_links": ["mechanics/combat/combat-implants.yaml"], "assets": []}, {"id": "history", "title": "История и ключевые события", "body": "Вступил в Valentinos в 2045 году, прошёл путь от Aspirante до главного Jefe к 2060-му. Поддерживает порядок в Heywood, ведёт переговоры с корпорациями и другими бандами, сохраняя баланс между честью и прагматизмом.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-persistence.yaml"], "assets": []}, {"id": "motivation", "title": "Ценности, цели и страхи", "body": "Стремится защитить Heywood и латиноамериканское сообщество, укрепить традиции и расширить влияние Valentinos преимущественно мирными методами. Больше всего опасается предательства и раскола внутри банды.\\n", "mechanics_links": ["mechanics/social/relationships-system.yaml"], "assets": []}, {"id": "personality", "title": "Личность и коммуникация", "body": "Харизматичный, принципиальный и ориентированный на семью лидер. Общается уважительно, часто использует испанские выражения «La familia lo es todo» и «Honor y respeto». Добродушен к союзникам, но жёсток к врагам.\\n", "mechanics_links": [], "assets": []}, {"id": "narrative_hooks", "title": "Игровые хуки", "body": "NPC может инициировать задания по защите района, культурные мероприятия, переговоры с другими фракциями и сценарии с выбором между чести и выгодой. Также подходит для сюжетов о поддержке семей бойцов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертировано описание El Jefe Valentinos в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

