INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-tyger-houses-master-2063-2080', 'Мастер домов Tyger Claws (2063-2080)', '{"metadata": {"id": "npc-tyger-houses-master-2063-2080", "title": "Мастер домов Tyger Claws (2063-2080)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "tyger-claws", "nightlife"], "topics": ["blackmail", "entertainment-network"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/tyger-claws/houses-master-2063-2080.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль «королевы ночи» Tyger Claws оставался в Markdown и не поддерживал сюжеты о контроле ночных клубов.", "goal": "Структурировать образ Мисато Такахаши для миссий компромата, переговоров и борьбы за рынки развлечений.", "essence": "Манипулятивная владычица клубов управляет секретами клиентов и расширяет влияние Tyger Claws через развлечения.", "key_points": ["Контролирует сеть клубов, борделей и казино Japantown.", "Использует компромат на корпоратов и политиков.", "Ведёт войны с конкурирующими фракциями за развлекательный рынок."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Мисато «Королева Ночи» Такахаши — мастер домов Tyger Claws. Элегантный внешний вид скрывает холодную расчётливость.\\nВладеет психотехникой и применяет компромат как главный инструмент влияния.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "operations", "title": "Сеть и операции", "body": "Управляет сетью заведений «Clouds», VIP-клубами и игровыми домами. Налаживает сделки с корпоратами, собирает\\nинформацию и использует её для шантажа или переговоров.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Стремится превратить развлекательную сеть Tyger Claws в инструмент глобального влияния. Боится утечек данных и\\nвмешательства конкурентов, оберегает репутацию «домов».\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Предлагает квесты «Секреты Облаков» и «Дом, который знает всё», открывая игроку возможности для компромата,\\nпереговоров и саботажа. Навыки: социальные — высокие, стратегические — высокие, боевые — средние.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Шантаж топ-менеджеров корпораций.\\n- Война за рынок развлечений с Mox или Valentinos.\\n- Расследование утечки данных из «Clouds».\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль мастера домов Tyger Claws в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

