INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-tyger-oyabun-2060-2077', 'Oyabun Tyger Claws (2060-2077)', '{"metadata": {"id": "npc-tyger-oyabun-2060-2077", "title": "Oyabun Tyger Claws (2060-2077)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["tyger-claws", "oyabun", "westbrook"], "topics": ["organized_crime", "entertainment_control"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "faction-tyger-claws-operations", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/tyger-claws/oyabun-2060-2077.md", "visibility": "internal", "audience": ["concept", "narrative", "economy"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно структурировать образ ойабуна Tyger Claws, контролирующего Japantown в 2060–2077 гг.", "goal": "Зафиксировать методы Хироми Сато, его влияние на клубы, BD-бизнес и взаимодействие с корпорациями.", "essence": "Элегантный и беспощадный лидер, балансирующий между криминальными доходами и легализацией части бизнеса.", "key_points": ["Управляет клубами, BD-сетями и охранными крышами Westbrook.", "Ведёт сделки с корпорациями и поддерживает коррумпированные связи с NCPD.", "Опасается внутренних заговоров и конкурентов вроде The Mox и Animals."]}, "content": {"sections": [{"id": "leadership_profile", "title": "Профиль лидерства", "body": "Хироми Сато — харизматичный ойабун Tyger Claws. Предпочитает традиционный стиль, сочетая кимоно и современные кибер-аксессуары.\\nПостоянно меняет локации между клубами Japantown, поддерживает лояльность через роскошь и страх.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "business_control", "title": "Контроль над бизнесом", "body": "- Сети BD и развлекательные площадки под контролем Tyger Claws.\\n- Охранные контракты и рэкет элитных заведений Westbrook.\\n- Финансовые и юридические схемы для «отмывки» средств и взаимодействия с корпорациями.\\nЭти направления формируют экономические и сюжетные миссии для игроков.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}, {"id": "motivations", "title": "Мотивации и угрозы", "body": "Мотивация — расширение влияния и превращение Tyger Claws в легальный бизнес-конгломерат.\\nОсновные угрозы — внутренние заговоры, конкуренция Mox и Animals, усиление контроля NCPD.\\nСато стремится стать неприкасаемым игроком City Center, сохранив якудза-традиции.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и баланс сил", "body": "- Arasaka: осторожные сделки, обмен информацией и услугами.\\n- The Mox: напряжённое соперничество за контроль клубов.\\n- NCPD: коррумпированные контакты для закрытия дел.\\nИгрок может усиливать или разрушать эти связи через цепочки квестов.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Квесты, связанные с клубами и BD-производством.\\n- Интриги внутри Tyger Claws и попытки переворота.\\n- Сделки с корпорациями, требующие дипломатии или саботажа.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль Oyabun Tyger Claws (2060–2077) в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

