INSERT INTO narrative.npc_definitions (npc_id, title, content_data, version, is_active) VALUES ('npc-voodoo-maman-2060-2077', 'Maman Brigitte — лидер Voodoo Boys (2060-2077)', '{"metadata": {"id": "npc-voodoo-maman-2060-2077", "title": "Maman Brigitte — лидер Voodoo Boys (2060-2077)", "document_type": "canon", "category": "npc-lore", "status": "draft", "version": "1.0.0", "last_updated": "2025-11-12T00:00:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["npc", "voodoo-boys", "pacifica"], "topics": ["netrunning-rituals", "community-protection"], "related_systems": ["narrative-service", "economy-service"], "related_documents": [{"id": "scenarios-factions-gangs-2020-2093", "relation": "references"}], "source": "shared/docs/knowledge/canon/narrative/npc-lore/factions/gangs/voodoo-boys/maman-brigitte-2060-2077.md", "visibility": "internal", "audience": ["concept", "narrative"], "risk_level": "high"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Профиль лидера Voodoo Boys существовал только в Markdown и не участвовал в сценариях Pacifica.", "goal": "Структурировать данные о Maman Brigitte для сюжетов нетраннинга и защиты общины.", "essence": "Визионер-нетраннер объединяет общину Pacifica и защищает её интересы через ритуалы в Сети.", "key_points": ["Руководит Voodoo Boys после упадка Pacifica и развивает сетевые культы.", "Ведёт юридические и силовые конфликты с корпорациями за контроль узлов.", "Предоставляет игроку доступ к уникальным нетраннерским квестам и ритуалам."]}, "content": {"sections": [{"id": "profile", "title": "Профиль", "body": "Maman Brigitte — духовный лидер и стратег Voodoo Boys. Поддерживает сеть ритуальных дата-алтарей в Pacifica,\\nсочетая традицию и высокие технологии. Координирует погружения в Сеть и распределяет ресурсы общины.\\n", "mechanics_links": ["mechanics/world/world-state/player-impact-systems.yaml"], "assets": []}, {"id": "history", "title": "История и достижения", "body": "После Четвёртой корпоративной войны собрала вокруг себя ядро нетраннеров, восстановила каналы связи общины и\\nорганизовала защиту от корп-сканеров. Заключила сделки с фиксёрами Pacifica и отражала попытки Militech и\\nArasaka взять район под контроль.\\n", "mechanics_links": [], "assets": []}, {"id": "motivation", "title": "Мотивация и цели", "body": "Заинтересована в автономии общины и сохранении сакральных знаний. Боится утечек ключей и профанации ритуалов.\\nЦель — создать собственный сегмент NET, недоступный корпорациям и чужакам.\\n", "mechanics_links": [], "assets": []}, {"id": "relationships", "title": "Связи и противостояния", "body": "- Mr. Hands — прагматичные договорённости по защите районов.\\n- Militech и Arasaka — враждебные стороны, пытающиеся проникнуть в их сеть.\\n- Maelstrom — опасные деловые контакты.\\n- The Mox — взаимная поддержка и обмен информацией.\\n", "mechanics_links": [], "assets": []}, {"id": "gameplay", "title": "Игровая роль", "body": "Выдаёт нетраннерские цепочки, глубокие погружения и задания по защите узлов. Навыки: нетраннинг — экстремально,\\nсоциальные — высокие, боевые — низкие (опирается на охрану общины). Может открывать уникальные ритуальные\\nмодификаторы и ключи для игроков.\\n", "mechanics_links": ["mechanics/quests/quest-structure.yaml", "mechanics/combat/combat-hacking-combat-integration.yaml"], "assets": []}, {"id": "narrative_hooks", "title": "Наративные хуки", "body": "- Ритуальные погружения в чёрный ICE.\\n- Защита общины от корпоративных рейдов.\\n- Выбор между сохранением традиций и технологическим прогрессом.\\n", "mechanics_links": ["mechanics/economy/economy-contracts.yaml"], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 133, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-12", "author": "concept_director", "changes": "Конвертирован профиль Maman Brigitte в формат знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (npc_id) DO UPDATE SET title = EXCLUDED.title, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

