INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-la-hollywood-sign', 'Лос-Анджелес — Знак Голливуда', 'Игрок обходит охрану, взбирается к буквам на холме и размещает собственный ретранслятор, получая доступ к медиасети.', 'side', 10, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "reach_hollywood_hills", "text": "Добраться до Hollywood Hills и подъёма на холм", "type": "travel", "target": "hollywood_hills", "count": 1, "optional": false}, {"id": "avoid_security", "text": "Избежать камер и патрулей охраны", "type": "stealth", "target": "security_patrols", "count": 1, "optional": false}, {"id": "climb_letter_o", "text": "Забраться внутрь буквы «O» (около 15 метров высоты)", "type": "interact", "target": "hollywood_sign_letter_o", "count": 1, "optional": false}, {"id": "install_hacker_node", "text": "Установить хакерский узел и протестировать сигнал", "type": "interact", "target": "hacker_node_installation", "count": 1, "optional": false}, {"id": "take_selfie", "text": "Сделать селфи для социальных сетей — повышает престиж", "type": "interact", "target": "social_media_upload", "count": 1, "optional": true}]'::jsonb, '{"experience": 1500, "money": 5000, "reputation": {}, "items": [], "unlocks": {"achievements": ["Король Голливуда"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-la-hollywood-sign", "title": "Лос-Анджелес — Знак Голливуда", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:40:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:15:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["los_angeles", "hollywood", "hacking"], "topics": ["regional-content", "infiltration"], "related_systems": ["stealth-system", "hacking-system"], "related_documents": [{"id": "canon-detailed-events-2020-2030", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/los-angeles/2020-2029/quest-001-hollywood-sign.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно начать голливудскую линейку квестов с иконического символа города.", "goal": "Предложить стелс-активность с установкой хакерского узла в знаке Hollywood.", "essence": "Игрок обходит охрану, взбирается к буквам на холме и размещает собственный ретранслятор, получая доступ к медиасети.", "key_points": ["Стелс и паркур по холмам Griffith Park.", "Внутри буквы «O» устанавливается узел, расширяющий влияние игрока.", "Награда — 1,500 XP и достижение «Король Голливуда»."]}, "quest_definition": {"quest_type": "side", "level_min": 10, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "reach_hollywood_hills", "text": "Добраться до Hollywood Hills и подъёма на холм", "type": "travel", "target": "hollywood_hills", "count": 1, "optional": false}, {"id": "avoid_security", "text": "Избежать камер и патрулей охраны", "type": "stealth", "target": "security_patrols", "count": 1, "optional": false}, {"id": "climb_letter_o", "text": "Забраться внутрь буквы «O» (около 15 метров высоты)", "type": "interact", "target": "hollywood_sign_letter_o", "count": 1, "optional": false}, {"id": "install_hacker_node", "text": "Установить хакерский узел и протестировать сигнал", "type": "interact", "target": "hacker_node_installation", "count": 1, "optional": false}, {"id": "take_selfie", "text": "Сделать селфи для социальных сетей — повышает престиж", "type": "interact", "target": "social_media_upload", "count": 1, "optional": true}], "rewards": {"experience": 1500, "money": 5000, "reputation": {}, "items": [], "unlocks": {"achievements": ["Король Голливуда"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side, сложность: medium, формат: solo, длительность: 1-2 часа.\\nНаграды: 1,500 XP, 5,000 едди, достижение «Король Голливуда».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до Hollywood Hills и подъёма на холм.\\n2. Избежать камер и патрулей охраны.\\n3. Забраться внутрь буквы «O» (около 15 метров высоты).\\n4. Установить хакерский узел и протестировать сигнал.\\n5. Сделать селфи для социальных сетей — повышает престиж.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Hollywood Sign — символ мировой киноиндустрии с 1923 года.\\nС площадки открывается панорама Лос-Анджелеса и долины.\\nОригинальное название — Hollywoodland; сохранившиеся опоры можно использовать как укрытия.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные связи", "body": "Установка узла расширяет сетевое покрытие игрока и открывает миссии медиаменеджмента.\\nКвест вводит в голливудскую сюжетную линию и усиливает взаимодействие с хакерскими системами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Hollywood Sign", "link": "https://www.hollywoodsign.org/"}], "decisions": []}, "implementation": {"github_issue": 1123, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Добавлен стартовый квест линейки Голливуда с акцентом на стелс и медиавлияние."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

