-- Quest: canon-quest-mexico-teotihuacan-pyramids
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-teotihuacan-pyramids', 'Мехико — Пирамиды Теотиуакана', 'Игрок отправляется в Теотиуакан, поднимается на Пирамиду Солнца и изучает древний город доацтекской цивилизации.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-teotihuacan-pyramids", "title": "Мехико — Пирамиды Теотиуакана", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T12:35:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:35:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["mexico_city", "archaeology", "exploration"], "topics": ["regional-content", "heritage"], "related_systems": ["quest-system", "exploration-system"], "related_documents": [{"id": "canon-detailed-events-2020-2030", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/mexico-city/2020-2029/quest-003-teotihuacan-pyramids.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание Теотиуакана находилось в Markdown и не подключалось к системам эксплоринга.", "goal": "Перенести квест на посещение пирамид Солнца и Луны в YAML с деталями маршрута.", "essence": "Игрок отправляется в Теотиуакан, поднимается на Пирамиду Солнца и изучает древний город доацтекской цивилизации.", "key_points": ["Квест-id MEXICO-2029-003, основная историко-эксплоринг цепочка.", "Включены Проспект Мёртвых и две ключевые пирамиды с панорамой.", "Награда даёт 3,000 XP и очки археологии."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main, сложность: medium, формат: solo, длительность: 4+ часов.\\nНаграды: 3,000 XP, -30 валюты, +25 к навыку «Археология», достижение «Исследователь Теотиуакана».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться из Мехико до Теотиуакана (≈50 км) и войти на Проспект Мёртвых.\\n2. Исследовать площадки целителей и храм Кецалькоатля.\\n3. Подняться по 248 ступеням на Пирамиду Солнца и зафиксировать панораму.\\n4. Спуститься к Пирамиде Луны и изучить ритуальные платформы.\\n5. Составить отчёт о связи города с доколумбовой цивилизацией.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Теотиуакан вырос в I веке н. э. и предшествовал ацтекам; Пирамида Солнца достигает 65 метров.\\nЗагадка строителей и точные астрономические ориентации делают локацию ключевой для археологических арок.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Связи и механики", "body": "Квест активирует археологические экспедиции, даёт доступ к исследовательским баффам и точкам быстрого перемещения.\\nМожет открыть ежедневные задания на раскопки и контракты с музеями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Teotihuacan", "link": "https://en.wikipedia.org/wiki/Teotihuacan"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Оформлен квест Теотиуакана в формате YAML с эксплоринг механиками."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

