INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-las-vegas-hoover-dam', 'Лас-Вегас 2020-2029 — Дамба Гувера', 'Игрок посещает плотину, изучает инженерные решения 1930-х годов и понимает зависимость города от воды и энергии.', 'side', 3, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "travel_to_dam", "text": "Добраться до Hoover Dam", "type": "travel", "target": "hoover_dam", "count": 1, "optional": false}, {"id": "take_tour", "text": "Провести тур по плотине: турбины, арт-деко архитектура", "type": "interact", "target": "dam_tour", "count": 1, "optional": false}, {"id": "learn_history", "text": "Изучить влияние дамбы на возникновение Лас-Вегаса", "type": "interact", "target": "dam_history_exhibit", "count": 1, "optional": false}]'::jsonb, '{"experience": 1500, "money": -100, "items": [{"item_id": "achievement_dam_witness", "quantity": 1}], "reputation": {}, "unlocks": []}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-las-vegas-hoover-dam", "title": "Лас-Вегас 2020-2029 — Дамба Гувера", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:30:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-05T00:00:00+00:00", "owners": [{"role": "narrative_lead", "contact": "narrative@necp.game"}], "tags": ["las-vegas", "hoover-dam", "infrastructure"], "topics": ["engineering", "regional-history"], "related_systems": ["narrative-service", "infrastructure-ops"], "related_documents": [{"id": "canon-quest-las-vegas-mob-museum", "relation": "contrasts"}, {"id": "canon-lore-timeline-author-index", "relation": "references"}, {"id": "github-issue-347", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/347", "relation": "migrated_to", "migrated_at": "2025-11-22T20:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/las-vegas/2020-2029/quest-008-hoover-dam.md", "visibility": "internal", "audience": ["narrative", "economy", "vision"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История дамбы Гувера была записана в Markdown и не использовалась в инфраструктурных арках.", "goal": "Описать влияние Hoover Dam на возникновение Лас-Вегаса в формате YAML.", "essence": "Игрок посещает плотину, изучает инженерные решения 1930-х годов и понимает зависимость города от воды и энергии.", "key_points": ["Дамба построена в 1931–1936 годах и обеспечивает Лас-Вегас водой и электричеством.", "Тур показывает турбины, арт-деко архитектуру и последствия засухи на озеро Мид.", "Награды повышают инженерный атрибут и фиксируют достижение «Свидетель Дамбы»."]}, "quest_definition": {"quest_type": "side", "level_min": 3, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "travel_to_dam", "text": "Добраться до Hoover Dam", "type": "travel", "target": "hoover_dam", "count": 1, "optional": false}, {"id": "take_tour", "text": "Провести тур по плотине: турбины, арт-деко архитектура", "type": "interact", "target": "dam_tour", "count": 1, "optional": false}, {"id": "learn_history", "text": "Изучить влияние дамбы на возникновение Лас-Вегаса", "type": "interact", "target": "dam_history_exhibit", "count": 1, "optional": false}], "rewards": {"experience": 1500, "money": -100, "items": [{"item_id": "achievement_dam_witness", "quantity": 1}], "reputation": {}, "unlocks": []}, "branches": []}, "content": {"sections": [{"id": "quest_overview", "title": "Описание", "body": "Квест отправляет игрока к дамбе Гувера, расположенной в 48 километрах от города. Плотина — ключ к существованию Лас-Вегаса\\nи символ инженерного прогресса времён Великой депрессии.\\n", "mechanics_links": [], "assets": []}, {"id": "quest_flow", "title": "Этапы", "body": "1. Поездка к плотине и обзор панорамного вида на границу Невады и Аризоны.\\n2. Экскурсия внутри дамбы: турбины, распределительные залы и арт-деко оформление.\\n3. Посещение озера Мид и обсуждение снижения уровня из-за засухи.\\n4. Финальная секция — анализ зависимости города от инфраструктуры и климатических рисков.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Фиксируются высота 221 метр, период строительства 1931–1936, образование озера Мид и роль дамбы в электроснабжении региона.\\nОтмечены вызовы климатических изменений и устойчивость Лас-Вегаса.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и механики", "body": "XP: 2500, Валюта: -30, Атрибут «Инженерия»: +25, достижение «Свидетель Дамбы». Механики — инженерный анализ, экология и историческое наследие.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 50, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлена полная структура quest_definition с objectives, rewards и unlocks. Обновлен github_issue на 50. Детализирован контент согласно архитектуре квестов."}, {"version": "1.0.0", "date": "2025-11-05", "author": "ai_brain_manager", "changes": "Квест «Дамба Гувера» перенесён в YAML и дополнен структурированным описанием."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requir