INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-frida-kahlo-house', 'Мехико — Casa Azul (Фрида Кало)', 'Игрок посещает Синий Дом, исследует картины, личные вещи и сад, открывая эмоциональные истории.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-frida-kahlo-house", "title": "Мехико — Casa Azul (Фрида Кало)", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T12:55:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:55:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["mexico_city", "art", "museum"], "topics": ["regional-content", "cultural-heritage"], "related_systems": ["social-system", "collectibles-system"], "related_documents": [{"id": "canon-quest-mexico-city-day-of-the-dead", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/mexico-city/2020-2029/quest-006-frida-kahlo-house.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Визит в Casa Azul не был структурирован в базе знаний.", "goal": "Описать музейный квест с акцентом на жизнь и творчество Фриды Кало.", "essence": "Игрок посещает Синий Дом, исследует картины, личные вещи и сад, открывая эмоциональные истории.", "key_points": ["Сюжет подчёркивает союз Фриды Кало и Диего Риверы.", "Комнаты музея демонстрируют боль и силу художницы.", "Квест даёт культурный бафф и артефакт коллекции."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 1-2 часа.\\nНаграды: 1,200 XP, -15 валюты, +20 к атрибуту «Культура», достижение «Ценитель Фриды».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть в район Койоакан и занять очередь в Casa Azul.\\n2. Осмотреть экспозицию автопортретов и личных артефактов.\\n3. Изучить мастерскую Фриды и инструменты для восстановления после травм.\\n4. Пройти по саду с мексиканскими растениями и декоративными черепами.\\n5. Получить цифровую репродукцию и запись комментариев экскурсовода.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Дом знаменит своим ярко-синим фасадом и атмосферой семейного творчества.\\nАвтопортреты отражают боль и идентичность художницы, в том числе монобровь и мексиканские мотивы.\\nВ музее сохранены личные вещи, костюмы и коляска, в которой Фрида работала после аварии.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест открывает коллекционные карточки искусства и повышает социальную репутацию среди культурных NPC.\\nМожет запускать цепочки, связанные с арт-аукционами и выставками.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Frida Kahlo Museum", "link": "https://www.museofridakahlo.org.mx/en/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован музейный квест Casa Azul в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLU