-- Quest: canon-quest-vegas-what-happens
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-vegas-what-happens', 'Лас-Вегас — What happens in Vegas', 'Команда игроков погружается в 24/7 хаос города: казино, клубы, бассейн-вечеринки и события, которые лучше оставить в секрете.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-vegas-what-happens", "title": "Лас-Вегас — What happens in Vegas", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T12:12:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:12:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["las_vegas", "nightlife", "social"], "topics": ["regional-content", "social-events"], "related_systems": ["social-system", "reputation-system"], "related_documents": [{"id": "canon-detailed-events-2020-2030", "relation": "references"}, {"id": "github-issue-347", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/347", "relation": "migrated_to", "migrated_at": "2025-11-22T20:15:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/las-vegas/2020-2029/quest-010-what-happens-in-vegas.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Нужно описать ключевой социальный опыт Лас-Вегаса, который поддерживает его легендарный слоган.", "goal": "Сконструировать групповую активность на 24 часа с акцентом на беспрерывные развлечения и риск.", "essence": "Команда игроков погружается в 24/7 хаос города: казино, клубы, бассейн-вечеринки и события, которые лучше оставить в секрете.", "key_points": ["Раскрывается легендарный слоган «What happens in Vegas, stays in Vegas».", "Предусмотрены финансовые траты, размытые воспоминания и социальные модификаторы.", "Квест рассчитан на группу из 2-4 игроков и награждает титулом «Вегас Survivor»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main, сложность: medium, формат: группа 2-4 игроков, длительность: более 24 игровых часов.\\nНаграды: 5,000 XP, минус 1,000-5,000 валюты, размытые воспоминания как сюжетный эффект, достижение «Вегас Survivor».\\n", "mechanics_links": [], "assets": []}, {"id": "itinerary", "title": "Дневник 24/7", "body": "1. Прибытие в город группой друзей или корпоративной командой.\\n2. Ночная сессия в казино и элитных клубах.\\n3. Дневные бассейн-вечеринки и шоу.\\n4. Круглосуточный доступ к алкоголю и развлечениям, где легко потерять контроль.\\n5. Финал с пробелами в памяти и невозможностью раскрыть детали.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные особенности", "body": "Лас-Вегас поддерживает 24/7 экономику развлечений.\\nКультура мальчишников, девичников и избыточности усиливает ощущение «греховного» города.\\nСекретность событий является частью бренда и влияет на репутационные механики.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Геймплейные связи", "body": "Квест задействует социальные метрики, влияние алкоголя, случайные события и риск потери ресурсов.\\nМожет открывать дальнейшие сценарии с шантажом, скрытыми контактами или уникальными контрактами.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Las Vegas Slogan History", "link": "https://www.visitlasvegas.com/"}], "decisions": []}, "implementation": {"github_issue": 347, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Описан групповой социальный опыт Лас-Вегаса, интегрированный с системами репутации и риска."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

