-- Quest: quest-atlanta-2029-010-olympics-1996
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-atlanta-2029-010-olympics-1996', 'Атланта — Наследие Олимпиады 1996', 'Игрок исследует Centennial Olympic Park, вспоминает ключевые моменты игр и принимает решения о развитии инфраструктуры.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-atlanta-2029-010-olympics-1996", "title": "Атланта — Наследие Олимпиады 1996", "document_type": "canon", "category": "narrative", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-22T19:16:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["atlanta", "olympics", "sports"], "topics": ["timeline_author", "mega-events"], "related_systems": ["narrative-service", "events-service", "economy-service"], "related_documents": [{"id": "github-issue-297", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/297", "relation": "migrated_to", "migrated_at": "2025-11-22T19:16:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/atlanta/2020-2029/quest-010-olympics-1996.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Наследие Олимпиады 1996 описано в заметке и не задействует спортивные и событийные механики Атланты.", "goal": "Формализовать квест, показывающий влияние игр на город и связывающий историю с современными событиями.", "essence": "Игрок исследует Centennial Olympic Park, вспоминает ключевые моменты игр и принимает решения о развитии инфраструктуры.", "key_points": ["1996 год стал столетием современных Олимпиад и вывел Атланту на глобальную сцену.", "Речь Мухаммеда Али и теракт в парке создают эмоциональный контраст.", "Инфраструктурное наследие открывает новые спортивные линии.", "Квест усиливает исторический показатель и спортивную идентичность города."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест проводит игрока по ключевым объектам Олимпиады 1996 года. Он изучает архивы, взаимодействует с NPC-волонтёрами\\nи оценивает, как игры повлияли на экономику и культуру Атланты.\\n", "mechanics_links": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "- Centennial Olympic Park и Fountain of Rings — символические места игр.\\n- Мухаммед Али, зажигавший олимпийский огонь, стал символом мужества.\\n- Теракт 27 июля 1996 года подчёркивает цену безопасности.\\n- Олимпиада модернизировала транспорт, стадионы и гостиницы города.\\n", "mechanics_links": []}, {"id": "quest_flow", "title": "Этапы прохождения", "body": "1. Посетить Centennial Olympic Park и прослушать хронику столетних игр.\\n2. Осмотреть Fountain of Rings и принять участие в интерактивном шоу.\\n3. Встретиться с NPC-волонтёрами и ветеранами игр, обсуждая организацию и эмоции.\\n4. Расследовать события теракта, чтобы отдать дань памяти пострадавшим.\\n5. Сделать выбор, как использовать олимпийское наследие в будущем контенте.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Игрок получает 3000 опыта, увеличивает показатель «История» на 25 и открывает достижение «Свидетель Олимпиады».\\nДенежных затрат нет, но открываются спортивные ивенты.\\n", "mechanics_links": []}, {"id": "mechanics", "title": "Механики и темы", "body": "Исторические исследования, спортивные события и управление наследием. Игрок решает, какие объекты сохранить,\\nмодернизировать или превратить в новые арены.\\n", "mechanics_links": []}, {"id": "connections", "title": "Связи и расширения", "body": "Квест интегрируется в спортивную линию Атланты, открывая liveops-турниры, фестивали и сценарии по развитию инфраструктуры.\\n", "mechanics_links": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 297, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конверсия квеста Legacy Olympics 1996 в структурированный YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

COMMIT;

