INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-la-freeway-chase', 'Лос-Анджелес — Погоня по фривею', 'Игрок угоняет корп-груз и прорывается через пробки, избегая полиции и дронов.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-la-freeway-chase", "title": "Лос-Анджелес — Погоня по фривею", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T12:22:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:22:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["los_angeles", "action", "driving"], "topics": ["regional-content", "pursuit"], "related_systems": ["quest-system", "vehicle-system"], "related_documents": [{"id": "canon-detailed-events-2020-2030", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/los-angeles/2020-2029/quest-009-freeway-chase.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Не хватало миссии, демонстрирующей легендарные погони по фривеям Лос-Анджелеса.", "goal": "Смоделировать кинематографичную погоню через I-405, I-10 и US-101.", "essence": "Игрок угоняет корп-груз и прорывается через пробки, избегая полиции и дронов.", "key_points": ["Квест-id LA-2029-009, период 2020-2029.", "Включены многоуровневые фривеи и культовые трафиковые заторы.", {"Награда": "3,000 XP, 20,000 едди и титул «Король Фривея»."}]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side, сложность: hard, формат: solo, длительность: 1-2 часа.\\nНаграды: 3,000 XP, 20,000 едди, достижение «Король Фривея».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Украсть конфискованный корпоративный груз и активировать ложные номера.\\n2. Прорываться по I-405, уклоняясь от патрулей и вертолётов.\\n3. Сменить трассу на I-10 и US-101, используя пробки как прикрытие.\\n4. Выполнить трюки на развязках, отключить трекинг груза и сбросить преследователей.\\n5. Сбросить машину в Downtown LA и скрыться в переулках.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Лос-Анджелес известен многоуровневыми фривеями, пробками и культовыми ТВ-погонами.\\nПромышленная зона Downtown и сеть шоссе создают уникальную сцену для автомобильных миссий.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Связи и механики", "body": "Квест интегрируется с системой транспортных дебаффов и полицейского преследования.\\nОткрывает тюнинг для уличных машин и сценарии с медийным освещением.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Los Angeles Freeway System", "link": "https://en.wikipedia.org/wiki/Los_Angeles_freeway_system"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлен экшен-квест про погоню по фривеям Лос-Анджелеса."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

