INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-la-movie-studio-tour', 'Лос-Анджелес — Тур по киностудии', 'Игрок погружается в мир производства кино, посещает backlot и может попасть в кадр как приглашённая звезда.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-la-movie-studio-tour", "title": "Лос-Анджелес — Тур по киностудии", "document_type": "canon", "category": "lore-quests", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T14:40:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T14:40:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["los-angeles", "hollywood", "studio-tour"], "topics": ["entertainment-industry", "tourism"], "related_systems": ["event-service", "fame-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/los-angeles/2020-2029/quest-007-movie-studio-tour.md", "visibility": "internal", "audience": ["concept", "narrative", "gameplay"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экскурсионный квест по киностудии был вне YAML-структуры и не прошёл схемную проверку.", "goal": "Описать тур по студии Universal/Warner в YAML, включая этапы, реквизит и возможное камео.", "essence": "Игрок погружается в мир производства кино, посещает backlot и может попасть в кадр как приглашённая звезда.", "key_points": ["Тур охватывает культовые декорации и реквизит Universal/Warner Bros.", "Предусмотрена встреча с актёром и участие в пробной съёмке.", "Награды включают опыт, славу и ачивку «За Кулисами Голливуда»."]}, "content": {"sections": [{"id": "overview", "title": "Обзор", "body": "Голливудские студии открывают двери для туристов, показывая производство фильмов.\\nКвест предлагает смесь образовательного контента, фан-сервисных моментов и лёгких мини-игр.\\n", "mechanics_links": [], "assets": []}, {"id": "flow", "title": "Ход события", "body": "1. Приобрести билет и пройти инструктаж.\\n2. Прокатиться на трамвайчике по backlot: улица Нью-Йорка, дом из «Psycho», аттракцион «Jaws».\\n3. Посетить активную площадку со съёмочной группой.\\n4. Встретить актёра или актрису, получить автограф.\\n5. Пройти пробную съёмку, сыграв камео в текущем проекте.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "- Реалистичные декорации городов и улиц.\\n- Студии с green screen и спецэффектами.\\n- Хранилища реквизита от классических фильмов.\\n- Настроения «Lights, camera, action!» на каждой площадке.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 2,000 опыта.\\n- Минус 100 €$ за билет и сувениры.\\n- +10 славы при успешном камео.\\n- Ачивка «За Кулисами Голливуда».\\n", "mechanics_links": [], "assets": []}, {"id": "mechanics", "title": "Механики", "body": "- Туристический сценарий с образовательными вставками.\\n- Мини-игры: управление камерой, ретейки, реакции на режиссёрские указания.\\n- Камео как дополнительная проверка навыков выступления.\\n", "mechanics_links": [], "assets": []}, {"id": "links", "title": "Связи", "body": "Связан с голливудской индустриальной линией и карьерными арками актёров.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"needs_task": false, "github_issue": 49, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Оформлен квест Movie Studio Tour в структуре знаний."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

