INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-mariachi-music', 'Мехико — Музыка марьячи', 'Игрок отправляется на Plaza Garibaldi, нанимает марьячи и погружается в романтические мелодии.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-mariachi-music", "title": "Мехико — Музыка марьячи", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T12:58:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T12:58:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["mexico_city", "music", "culture"], "topics": ["regional-content", "live-performance"], "related_systems": ["social-system", "economy-system"], "related_documents": [{"id": "canon-quest-mexico-city-tacos-al-pastor", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/mexico-city/2020-2029/quest-007-mariachi-music.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест про Plaza Garibaldi и музыку марьячи не был занесён в YAML.", "goal": "Оформить музыкальное событие с наймом ансамбля и социальными эффектами.", "essence": "Игрок отправляется на Plaza Garibaldi, нанимает марьячи и погружается в романтические мелодии.", "key_points": ["Описаны костюмы чарро и репертуар марьячи.", "Нанимается группа за песню, влияя на экономику и настроение.", "Награда добавляет культурные очки и достижение ценителя."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 1-2 часа.\\nНаграды: 1,000 XP, -30 валюты, +15 к атрибуту «Культура», достижение «Ценитель Марьячи».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить Plaza Garibaldi вечером и оценить выступления.\\n2. Выбрать ансамбль и договориться о репертуаре (románticas, rancheras, clásicos).\\n3. Нанять группу за 20-50 едди и насладиться живым исполнением.\\n4. Присоединиться к танцу или сделать фильмовые записи для соцсетей.\\n5. Поблагодарить музыкантов, поддержав чаевыми и рекомендациями.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Plaza Garibaldi считается меккой марьячи в Мехико.\\nМузыканты выступают в костюмах чарро с серебряной отделкой, используя скрипки, трубы и гитары.\\nРепертуар включает «Cielito Lindo», «La Bamba» и романтические серенады.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест взаимодействует с системой экономики развлечений и повышает отношения с культурными NPC.\\nМожет запускать музыкальные ивенты, контракты на выступления и коллекции инструментов.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Plaza Garibaldi", "link": "https://en.wikipedia.org/wiki/Plaza_Garibaldi"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован музыкальный квест Plaza Garibaldi в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requi