INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-la-malibu-mansion-party', 'Лос-Анджелес — Malibu Mansion Party', 'Игрок получает приглашение на роскошную вечеринку в Малибу и раскрывает секреты звезд.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-la-malibu-mansion-party", "title": "Лос-Анджелес — Malibu Mansion Party", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-12-14T12:40:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-12-14T12:40:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["los_angeles", "party", "malibu", "celebrity"], "topics": ["regional-content", "social-climbing"], "related_systems": ["social-system", "reputation-system"], "related_documents": [{"id": "canon-quest-la-beverly-hills-heist", "relation": "references"}], "source": "knowledge/canon/lore/_03-lore/timeline-author/quests/america/los-angeles/2020-2029/quest-015-malibu-mansion-party.yaml", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "content_writer", "reviewer": "Content Writer Agent", "reviewed_at": "2025-12-14T12:40:00+00:00", "status": "approved"}], "next_actions": []}, "summary": {"problem": "Нужно показать гламурную жизнь знаменитостей в Малибу и контраст с обычной жизнью.", "goal": "Создать квест о проникновении на эксклюзивную вечеринку в особняке знаменитости.", "essence": "Игрок получает приглашение на роскошную вечеринку в Малибу и раскрывает секреты звезд.", "key_points": ["Малибу как прибежище знаменитостей и сверхбогатых.", "Вечеринки как место для networking и интриг.", "Контраст между показным богатством и скрытыми пороками."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 30-45 минут.\\nНаграды: 600 XP, -10 единиц валюты, полезные контакты, достижение «Party Crasher».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить фальшивое приглашение или поддельные документы для входа.\\n2. Прибыть к роскошному особняку на побережье Малибу.\\n3. Пройти через охрану и слиться с толпой знаменитостей.\\n4. Подслушать сплетни, собрать компромат или украсть ценности.\\n5. Избежать обнаружения частной охраной.\\n6. Уйти с добычей до прибытия полиции.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Малибу — эксклюзивный район с частными пляжами и особняками знаменитостей.\\nВечеринки здесь — место для демонстрации статуса и заключения сделок.\\nОхрана тщательно проверяет гостей, но всегда есть способы обойти систему.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест повышает социальные навыки и дает доступ к элитным кругам.\\nМожет открыть цепочку квестов о шпионаже среди знаменитостей и корпоративных интригах.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Malibu Mansion Party", "definition": "Эксклюзивная вечеринка в особняке знаменитости в Малибу"}], "references": [{"title": "Malibu Celebrity Culture", "link": "https://en.wikipedia.org/wiki/Malibu,_California"}], "decisions": []}, "implementation": {"github_issue": 140876045, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-12-14", "author": "content_writer_agent", "changes": "Создан квест о проникновении на вечеринку знаменитостей в Малибу."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

