INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-montreal-french-language', 'Монреаль — Квебекский французский', 'Игрок записывается на курс, осваивает квебекские фразы и тестирует их в кафе Монреаля.', 'side', 2, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "enroll_quebec_french_course", "text": "Записаться на интенсивный курс квебекского французского", "type": "interact", "target": "quebec_french_course", "count": 1, "optional": false}, {"id": "master_pronunciation_differences", "text": "Освоить ключевые отличия от парижского варианта и практиковать произношение", "type": "interact", "target": "pronunciation_practice", "count": 1, "optional": false}, {"id": "learn_daily_phrases", "text": "Разучить повседневные фразы и характерные междометия", "type": "interact", "target": "quebec_expressions_learning", "count": 1, "optional": false}, {"id": "practice_at_cafe", "text": "Провести практику в локальном кафе, соблюдая требования Закона 101", "type": "interact", "target": "bill_101_cafe_practice", "count": 1, "optional": false}, {"id": "take_language_exam", "text": "Сдать мини-экзамен с носителем языка и получить рекомендацию сообщества", "type": "interact", "target": "native_speaker_exam", "count": 1, "optional": false}]'::jsonb, '{"xp": 2500, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "quebecois", "name": "Квебекуа"}], "skill_boosts": {"french_language": 20}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-montreal-french-language", "title": "Монреаль — Квебекский французский", "document_type": "canon", "category": "quest", "status": "approved", "version": "2.0.0", "last_updated": "2025-11-29T13:30:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T13:30:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["montreal", "language", "culture"], "topics": ["regional-content", "education"], "related_systems": ["social-system", "progression-system"], "related_documents": [{"id": "canon-quest-montreal-canadiens", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/montreal/2020-2029/quest-002-french-language.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о квебекском французском существовал только в Markdown без связей.", "goal": "Структурировать языковую активность с акцентом на Закон 101 и живое общение.", "essence": "Игрок записывается на курс, осваивает квебекские фразы и тестирует их в кафе Монреаля.", "key_points": ["Квест-id MONTREAL-2029-002, социально-образовательная линия.", "Включены особенности акцента, Закон 101 и культурный сленг.", "Награда усиливает языковой навык и открывает франкофонские контакты."]}, "quest_definition": {"quest_type": "side", "level_min": 2, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "enroll_quebec_french_course", "text": "Записаться на интенсивный курс квебекского французского", "type": "interact", "target": "quebec_french_course", "count": 1, "optional": false}, {"id": "master_pronunciation_differences", "text": "Освоить ключевые отличия от парижского варианта и практиковать произношение", "type": "interact", "target": "pronunciation_practice", "count": 1, "optional": false}, {"id": "learn_daily_phrases", "text": "Разучить повседневные фразы и характерные междометия", "type": "interact", "target": "quebec_expressions_learning", "count": 1, "optional": false}, {"id": "practice_at_cafe", "text": "Провести практику в локальном кафе, соблюдая требования Закона 101", "type": "interact", "target": "bill_101_cafe_practice", "count": 1, "optional": false}, {"id": "take_language_exam", "text": "Сдать мини-экзамен с носителем языка и получить рекомендацию сообщества", "type": "interact", "target": "native_speaker_exam", "count": 1, "optional": false}], "rewards": {"xp": 2500, "currency": 0, "items": [], "reputation": {}, "achievements": [{"id": "quebecois", "name": "Квебекуа"}], "skill_boosts": {"french_language": 20}}, "branches": []}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: medium, формат: solo, длительность: 2-4 часа.\\nНаграды: 2,500 XP, 0 валюты, +20 к навыку «Французский», достижение «Квебекуа».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Записаться на интенсивный курс квебекского французского.\\n2. Освоить ключевые отличия от парижского варианта и практиковать произношение.\\n3. Разучить повседневные фразы и характерные междометия («Tabarnak!» использовать с осторожностью).\\n4. Провести практику в локальном кафе, соблюдая требования Закона 101.\\n5. Сдать мини-экзамен с носителем языка и получить рекомендацию сообщества.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "В Монреале более 65% франкофонов; Закон 101 закрепляет французский как основной.\\nКвебекский акцент и идиомы формируют отдельную культурную идентичность.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Связи и механики", "body": "Квест повышает доступ к франкофонским NPC и открывает ветки дипломатии с квестами города.\\nЯзыковой прогресс влияет на социализацию, скидки в магазинах и сюжет с двуязычными мероприятиями.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Charter of the French Language (Bill 101)", "link": "https://www.oqlf.gouv.qc.ca/english/charter/"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "2.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован квест изучения квебекского французского в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

