BEGIN;

-- Quest: canon-quest-paris-2020-2029-catacombs-exploration
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-paris-2020-2029-catacombs-exploration', 'Париж 2020-2029 — «Исследование Катакомб»', 'Квест сочетает хоррор, навигацию и взаимодействие с катафилами — обитателями подземелий.', 'side', 4, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-paris-2020-2029-catacombs-exploration", "title": "Париж 2020-2029 — «Исследование Катакомб»", "document_type": "canon", "category": "timeline-author", "status": "content-ready", "version": "2.0.0", "last_updated": "2025-11-23T12:00:00Z", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["paris", "catacombs", "exploration"], "topics": ["urban-legend", "underground"], "related_systems": ["narrative-service", "survival-module"], "related_documents": [{"id": "github-issue-617", "title": "GitHub Issue #617", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/617", "relation": "migrated_to", "migrated_at": "2025-11-22T23:40:00Z"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/europe/paris/2020-2029/quest-007-catacombs-exploration.md", "visibility": "internal", "audience": ["concept", "narrative", "liveops"], "risk_level": "medium"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Для Парижа требуется мрачный эксплоринг, задействующий знаменитые катакомбы и угрозу потеряться.", "goal": "Позволить группе игроков исследовать нелегальные туннели и найти легендарную комнату Z.", "essence": "Квест сочетает хоррор, навигацию и взаимодействие с катафилами — обитателями подземелий.", "key_points": ["300 километров туннелей и 6 миллионов скелетов создают атмосферу «Империи смерти».", "Катафилы выступают проводниками или противниками, добавляя социальную динамику.", "Экспедиция может завершиться находкой артефактов или пропажей партии."]}, "quest_definition": {"quest_type": "side", "level_min": 4, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "find_illegal_entry", "text": "Найти нелегальный вход в катакомбы и спуститься на 20 метров под землю", "type": "navigate", "target": "catacombs_illegal_entry", "count": 1, "optional": false}, {"id": "navigate_tunnels", "text": "Навигация по 300 километрам туннелей, избегая заблудиться", "type": "navigate", "target": "catacombs_tunnels", "count": 1, "optional": false}, {"id": "interact_cataphiles", "text": "Взаимодействовать с катафилами - проводниками или противниками", "type": "dialogue", "target": "catacombs_cataphiles", "count": 1, "optional": false}, {"id": "find_room_z", "text": "Найти легендарную комнату Z с артефактами", "type": "interact", "target": "catacombs_room_z", "count": 1, "optional": false}, {"id": "escape_catacombs", "text": "Выбраться из катакомб с артефактами, не потеряв партию", "type": "navigate", "target": "catacombs_exit", "count": 1, "optional": false}], "rewards": {"experience": 3500, "currency": 0, "items": [{"item_id": "catacombs_artifacts", "quantity": 1}], "reputation": {"Paris_Underground": 20}, "flags": ["catacombs_explorer", "room_z_discovered"]}, "branches": []}, "content": {"sections": [{"id": "stages", "title": "Последовательность этапов", "body": "Этап 1: Вход в катакомбы\nЛокация: Париж, вход в катакомбы (нелегальный)\nОписание: Игроки ищут нелегальный вход и спускаются на 20 метров под землю. Первые коридоры вводят правила выживания и ограничения по освещению.\nЦели:\n  - Найти нелегальный вход в катакомбы\n  - Изучить историю катакомб (300 км туннелей, 6 миллионов скелетов)\n  - Подготовить снаряжение (фонари, карты, провизия)\n  - Спуститься на 20 метров под землю\n  - Войти в первые коридоры катакомб\n  - Адаптироваться к условиям подземелья (темнота, узкие проходы)\n  - Проверка навыков: Investigation 0.3+ для поиска входа, Awareness 0.3+ для понимания условий\n\nЭтап 2: Лабиринт катакомб\nЛокация: Париж, катакомбы, подземные туннели\nОписание: Подземный маршрут полон стен из черепов, латинских надписей и узких туннелей. Навигация требует отметок и периодических проверок на страх.\nЦели:\n  - Исследовать лабиринт катакомб\n  - Навигация по туннелям (стены из черепов, латинские надписи)\n  - Оставлять отметки для возвращения\n  - Проходить узкие туннели\n  - Преодолевать страх и клаустрофобию\n  - Изучать латинские надписи и символы\n  - Проверка навыков: Navigation 0.4+ для навигации, Perception 0.3+ для изучения надписей, Survival 0.3+ для преодоления страха\n\nЭтап 3: Встреча с катафилами\nЛокация: Париж, катакомбы, скрытые комнаты\nОписание: Катафилы могут предложить помощь или устроить засаду. Выбор взаимодействия влияет на доступ к скрытым комнатам.\nЦели:\n  - Встретиться с катафилами (обитатели подземелий)\n  - Определить намерения катафилов (помощь или вражда)\n  - Взаимодействовать с катафилами (диалог, торговля, конфликт)\n  - Получить информацию о скрытых комнатах\n  - Разблокировать доступ к секретным местам\n  - Проверка навыков: Social 0.3+ для взаимодействия, Stealth 0.3+ для избежания засады, Diplomacy 0.3+ для переговоров\n\nЭтап 4: Комната Z\nЛокация: Париж, катакомбы, легендарная комната Z\nОписание: Легендарная комната содержит артефакты и уникальные записи. Попадание туда требует решения головоломок и дополнительных проверок на устойчивость.\nЦели:\n  - Найти путь к легендарной комнате Z\n  - Решить головоломки для доступа к комнате\n  - Разгадать древние руны и символы\n  - Преодолеть дополнительные проверки на устойчивость\n  - Исследовать комнату Z (артефакты, записи)\n  - Забрать найденные артефакты\n  - Проверка навыков: Investigation 0.5+ для поиска, Puzzle 0.4+ для головоломок, Awareness 0.4+ для устойчивости\n\nЭтап 5: Возвращение на поверхность\nЛокация: Париж, катакомбы, выход на поверхность\nОписание: Возвращение на поверхность требует успешной навигации и избежания опасностей. Потеря пути может привести к статусу \"Пропавший\".\nЦели:\n  - Найти путь назад к выходу\n  - Использовать оставленные отметки для навигации\n  - Избежать опасностей подземелья\n  - Преодолеть страх и усталость\n  - Выйти на поверхность\n  - Сохранить найденные артефакты\n  - Проверка навыков: Navigation 0.4+ для возвращения, Survival 0.4+ для избежания опасностей", "mechanics_links": ["mechanics/exploration/darkness-navigation.yaml", "mechanics/survival/claustrophobia-checks.yaml", "mechanics/social/faction-reputation.yaml", "mechanics/puzzle/ancient-runes.yaml"]}, {"id": "npc_dialogues", "title": "Диалоги с NPC", "body": "Проводник (Люк Дюбуа)\nДиалог 1 (начальный):\n  - Люк: \"Вы хотите исследовать катакомбы? Это опасно. 300 километров туннелей, 6 миллионов скелетов... Вы готовы?\"\n  - Игрок: \"Да, я готов к вызову.\"\n  - Люк: \"Хорошо. Катакомбы — это не место для новичков. Здесь легко потеряться, и никто не найдёт вас. Вы понимаете риски?\"\n  - Игрок: \"Расскажите о катакомбах.\"\n  - Люк: \"Катакомбы Парижа — это подземный лабиринт под городом. Здесь покоятся останки миллионов людей. И здесь живут катафилы — те, кто знает эти туннели лучше, чем свои дома.\"\n\nКатафил (Мари)\nДиалог 2 (встреча в катакомбах):\n  - Мари: \"Кто вы? Вы не катафилы... Что вы здесь делаете?\"\n  - Игрок: \"Мы исследуем катакомбы.\"\n  - Мари: \"Исследуете? Катакомбы — это не музей. Это наш дом. Вы вторглись на нашу территорию.\"\n  - Игрок: \"Мы не хотим вредить.\"\n  - Мари: \"Хм... Возможно, я могу помочь вам. Но только если вы будете уважать наши правила. Вы ищете комнату Z?\"\n  - Игрок: \"Да, мы ищем комнату Z.\"\n  - Мари: \"Легендарная комната... Немногие находят её. Я могу показать путь, но это будет стоить.\"\n\nВраждебный катафил (Жан)\nДиалог 3 (засада):\n  - Жан: \"Остановитесь! Вы на нашей территории. Отдайте всё, что у вас есть, и мы позволим вам уйти.\"\n  - Игрок: \"Мы не хотим конфликта.\"\n  - Жан: \"Слишком поздно. Вы вторглись. Теперь вы будете платить.\"\n  - Игрок: \"Мы готовы к бою.\"\n  - Жан: \"Хорошо. Вы получили то, что заслужили. Но помните: в катакомбах легко потеряться, и никто не найдёт вас.\"\n\nИсследователь (Доктор Пьер Мартин)\nДиалог 4 (в комнате Z):\n  - Доктор Мартин: \"Вы нашли комнату Z... Я не думал, что кто-то ещё сможет найти её.\"\n  - Игрок: \"Вы тоже исследовали катакомбы?\"\n  - Доктор Мартин: \"Да, я исследовал катакомбы много лет. Комната Z — это легенда. Здесь хранятся артефакты и записи, которые могут изменить наше понимание истории.\"\n  - Игрок: \"Что вы знаете о комнате?\"\n  - Доктор Мартин: \"Комната Z содержит древние артефакты и уникальные записи. Но она защищена головоломками и ловушками. Только самые умные и смелые могут добраться до её секретов.\""}, {"id": "branches", "title": "Ветвления и выборы", "body": "Выбор 1: Способ входа в катакомбы\nУсловие: При поиске входа\nВарианты:\n  - Легальный вход: официальный вход с гидом, безопасно, но дороже и меньше свободы, разблокируется базовая информация\n  - Нелегальный вход: нелегальный вход без гида, рискованно, но дешевле и больше свободы, разблокируется скрытая информация\n  - С проводником: вход с опытным проводником, баланс безопасности и свободы, разблокируется дополнительная информация\n\nВыбор 2: Подход к навигации\nУсловие: При навигации по лабиринту\nВарианты:\n  - Аккуратная навигация: тщательно оставлять отметки, медленнее, но безопаснее, снижает риск потеряться\n  - Быстрая навигация: быстро двигаться, меньше отметок, быстрее, но рискованнее, увеличивает риск потеряться\n  - Сбалансированный подход: оптимальный баланс скорости и безопасности\n\nВыбор 3: Взаимодействие с катафилами (критический)\nУсловие: При встрече с катафилами\nВарианты:\n  - Дружелюбный подход: попытаться подружиться, может получить помощь, разблокируется доступ к скрытым комнатам, +10 репутация Cataphiles\n  - Агрессивный подход: быть готовым к бою, может избежать засады, но конфликт, -10 репутация Cataphiles\n  - Нейтральный подход: оставаться нейтральным, стандартное взаимодействие, баланс репутации\n\nВыбор 4: Подход к поиску комнаты Z\nУсловие: При поиске легендарной комнаты\nВарианты:\n  - Интенсивный поиск: тщательно искать, больше времени, но больше шансов найти комнату, разблокируется доступ к комнате Z\n  - Быстрый поиск: быстро искать, меньше времени, но меньше шансов найти комнату\n  - С помощью катафилов: попросить помощи у катафилов, требует хороших отношений, но увеличивает шансы найти комнату"}, {"id": "skill_checks", "title": "Проверки навыков", "body": "Проверка 1: Investigation (Этап 1)\nТребование: 0.3+\nУспех: Игрок находит лучший вход в катакомбы, разблокируется дополнительная информация о подземельях\nПровал: Игрок находит стандартный вход, получает только базовую информацию\n\nПроверка 1b: Awareness (Этап 1)\nТребование: 0.3+\nУспех: Игрок понимает все аспекты условий подземелья, разблокируется дополнительная информация о выживании\nПровал: Игрок получает только базовое понимание условий\n\nПроверка 2: Navigation (Этап 2)\nТребование: 0.4+\nУспех: Игрок успешно навигает по лабиринту, снижает риск потеряться на 50% и разблокируется достижение \"Мастер навигации\"\nПровал: Игрок испытывает трудности с навигацией, увеличивает риск потеряться\n\nПроверка 2b: Perception (Этап 2)\nТребование: 0.3+\nУспех: Игрок успешно изучает латинские надписи, разблокируется дополнительная информация о истории катакомб и разблокируется достижение \"Знаток латыни\"\nПровал: Игрок получает только базовое понимание надписей\n\nПроверка 2c: Survival (Этап 2)\nТребование: 0.3+\nУспех: Игрок успешно преодолевает страх и клаустрофобию, снижает риск психических эффектов на 30% и разблокируется достижение \"Храбрый исследователь\"\nПровал: Игрок испытывает страх и клаустрофобию, увеличивает риск психических эффектов\n\nПроверка 3: Social (Этап 3)\nТребование: 0.3+\nУспех: Игрок успешно взаимодействует с катафилами, получает помощь и разблокируется достижение \"Друг катафилов\"\nПровал: Игрок получает стандартное взаимодействие\n\nПроверка 3b: Stealth (Этап 3)\nТребование: 0.3+\nУспех: Игрок успешно избегает засады, предотвращает конфликт и разблокируется достижение \"Тень\"\nПровал: Игрок попадает в засаду, начинается конфликт\n\nПроверка 3c: Diplomacy (Этап 3)\nТребование: 0.3+\nУспех: Игрок успешно ведёт переговоры с катафилами, получает доступ к скрытым комнатам и разблокируется достижение \"Дипломат\"\nПровал: Игрок получает стандартный результат переговоров\n\nПроверка 4: Investigation (Этап 4)\nТребование: 0.5+\nУспех: Игрок находит путь к комнате Z, разблокируется доступ к легендарной комнате и разблокируется достижение \"Искатель легенд\"\nПровал: Игрок не находит комнату Z, пропускает награды\n\nПроверка 4b: Puzzle (Этап 4)\nТребование: 0.4+\nУспех: Игрок успешно решает головоломки, получает доступ к артефактам и разблокируется достижение \"Мастер головоломок\"\nПровал: Игрок не может решить головоломки, не получает доступ к артефактам\n\nПроверка 4c: Awareness (Этап 4)\nТребование: 0.4+\nУспех: Игрок успешно преодолевает дополнительные проверки на устойчивость, получает дополнительные награды и разблокируется достижение \"Несокрушимый\"\nПровал: Игрок испытывает трудности с устойчивостью, получает только базовые награды\n\nПроверка 5: Navigation (Этап 5)\nТребование: 0.4+\nУспех: Игрок успешно находит путь назад, избегает статуса \"Пропавший\" и разблокируется достижение \"Возвращение героя\"\nПровал: Игрок теряется, получает статус \"Пропавший\", требуется помощь\n\nПроверка 5b: Survival (Этап 5)\nТребование: 0.4+\nУспех: Игрок успешно избегает опасностей подземелья, сохраняет всех членов команды и разблокируется достижение \"Выживший\"\nПровал: Игрок теряет членов команды, увеличивается риск смерти"}, {"id": "rewards", "title": "Награды и эффекты", "body": "Базовые награды:\n  - Опыт: 3 000 XP\n  - Репутация Paris Exploration Community: +15\n  - Репутация Cataphiles: +5/+10/-10 (в зависимости от выбора)\n\nНаграды в зависимости от найденных артефактов:\n  - Редкие артефакты из комнаты Z: +1-3 уникальных артефакта (при успешном доступе к комнате)\n  - Элементы лора: +10 к культуре (при изучении надписей)\n  - Уникальные записи: +1 книга истории (при исследовании комнаты)\n\nРиски и последствия:\n  - Потеря членов команды: при провале проверки Survival на этапе 5, теряется 1-2 члена команды\n  - Статус \"Пропавший\": при провале проверки Navigation на этапе 5, игрок получает статус \"Пропавший\", требуется помощь для выхода\n  - Психические эффекты: при провале проверки Survival на этапе 2, игрок получает дебафф \"Клаустрофобия\" на 1 час\n\nДополнительные награды (при успешных проверках навыков):\n  - Дополнительная информация о катакомбах (при успешной проверке Investigation на этапе 1)\n  - Дополнительная информация о выживании (при успешной проверке Awareness на этапе 1)\n  - Достижение \"Мастер навигации\" (при успешной проверке Navigation на этапе 2)\n  - Снижение риска потеряться на 50% (при успешной проверке Navigation на этапе 2)\n  - Достижение \"Знаток латыни\" (при успешной проверке Perception на этапе 2)\n  - Дополнительная информация о истории (при успешной проверке Perception на этапе 2)\n  - Достижение \"Храбрый исследователь\" (при успешной проверке Survival на этапе 2)\n  - Снижение риска психических эффектов на 30% (при успешной проверке Survival на этапе 2)\n  - Достижение \"Друг катафилов\" (при успешной проверке Social на этапе 3)\n  - Достижение \"Тень\" (при успешной проверке Stealth на этапе 3)\n  - Достижение \"Дипломат\" (при успешной проверке Diplomacy на этапе 3)\n  - Доступ к скрытым комнатам (при успешной проверке Diplomacy на этапе 3)\n  - Достижение \"Искатель легенд\" (при успешной проверке Investigation на этапе 4)\n  - Доступ к комнате Z (при успешной проверке Investigation на этапе 4)\n  - Достижение \"Мастер головоломок\" (при успешной проверке Puzzle на этапе 4)\n  - Доступ к артефактам (при успешной проверке Puzzle на этапе 4)\n  - Достижение \"Несокрушимый\" (при успешной проверке Awareness на этапе 4)\n  - Дополнительные награды (при успешной проверке Awareness на этапе 4)\n  - Достижение \"Возвращение героя\" (при успешной проверке Navigation на этапе 5)\n  - Избежание статуса \"Пропавший\" (при успешной проверке Navigation на этапе 5)\n  - Достижение \"Выживший\" (при успешной проверке Survival на этапе 5)\n  - Сохранение всех членов команды (при успешной проверке Survival на этапе 5)\n\nНаграды за способ входа:\n  - Легальный вход: безопасность, базовая информация\n  - Нелегальный вход: свобода, скрытая информация, риск\n  - С проводником: баланс, дополнительная информация\n\nНаграды за подход к навигации:\n  - Аккуратная навигация: снижение риска потеряться, достижение \"Аккуратный исследователь\"\n  - Быстрая навигация: экономия времени, но увеличение риска\n  - Сбалансированный подход: оптимальный баланс\n\nНаграды за взаимодействие с катафилами:\n  - Дружелюбный подход: +10 репутация Cataphiles, доступ к скрытым комнатам, достижение \"Друг катафилов\"\n  - Агрессивный подход: -10 репутация Cataphiles, конфликт, но избежание засады\n  - Нейтральный подход: баланс репутации\n\nНаграды за подход к поиску комнаты Z:\n  - Интенсивный поиск: больше шансов найти комнату, доступ к комнате Z, артефакты\n  - Быстрый поиск: экономия времени, но меньше шансов найти комнату\n  - С помощью катафилов: увеличение шансов, но требует хороших отношений\n\nУникальные награды:\n  - Доступ к комнате Z (только при успешном поиске)\n  - Редкие артефакты из комнаты Z (только при успешном доступе)\n  - Уникальные записи истории (только при исследовании комнаты)\n  - Влияние на все последующие квесты исследования в Париже"}, {"id": "connections", "title": "Связи и продолжения", "body": "Квест влияет на исследовательские и социальные квесты в Париже.\n\nСвязанные квесты:\n  - \"Париж: тайны подземелья\" (разблокируется после успешного исследования)\n  - \"Париж: друзья катафилов\" (разблокируется при дружелюбном подходе)\n  - \"Париж: конфликт с катафилами\" (разблокируется при агрессивном подходе)\n  - \"Париж: легенда комнаты Z\" (разблокируется при нахождении комнаты)\n  - \"Париж: пропавшие исследователи\" (разблокируется при статусе \"Пропавший\")\n\nВлияние на репутацию:\n  - Paris Exploration Community: +15 (базовая)\n  - Cataphiles: +5/+10/-10 (в зависимости от выбора)\n  - Paris Cultural Community: +10 (при изучении надписей)\n  - Paris Survival Community: +5 (при успешном выживании)\n\nПоследствия для статуса:\n  - Статус \"Пропавший\": при провале проверки Navigation на этапе 5\n  - Статус \"Выживший\": при успешном выживании\n  - Дебафф \"Клаустрофобия\": при провале проверки Survival на этапе 2"}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 617, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-13", "author": "concept_director", "changes": "Конверсия квеста «Исследование Катакомб» из Markdown в YAML."}, {"version": "1.0.0", "date": "2025-11-23", "author": "content_writer", "changes": "Добавлен детальный контент: этапы с локациями и целями (5 этапов), диалоги с NPC (проводник, катафилы дружелюбный и враждебный, исследователь), проверки навыков (Investigation, Awareness, Navigation, Perception, Survival, Stealth, Social, Diplomacy, Puzzle), ветвления и выборы, детальные награды с рисками и последствиями."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 2, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

