INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-megacity-traffic-solutions', 'Mexico City 2020-2029 — «Решения транспортных проблем мегаполиса»', 'Мехико-сити как крупнейший мегаполис мира становится полигоном для транспортных инноваций.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-megacity-traffic-solutions", "title": "Mexico City 2020-2029 — «Решения транспортных проблем мегаполиса»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:59:46+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["mexico-city", "megacity", "traffic", "transportation", "infrastructure"], "topics": ["transportation", "urban-planning", "infrastructure"], "related_systems": ["narrative-service", "transportation-system", "urban-system"], "related_documents": [{"id": "canon-region-america-mexico-city-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/mexico-city/2020-2029/quest-011-megacity-traffic-solutions.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Мехико-сити борется с катастрофическими пробками с помощью гиперлуп-технологий, но сталкивается с сопротивлением традиционных транспортных компаний.", "goal": "Игрок помогает внедрить революционные транспортные решения и преодолевает сопротивление таксистов и автобусных компаний.", "essence": "Мехико-сити как крупнейший мегаполис мира становится полигоном для транспортных инноваций.", "key_points": ["Подземные гиперлуп-системы для быстрого передвижения.", "Конфликт между традиционным и новым транспортом.", "Финальная интеграция всех транспортных систем."]}, "content": {"sections": [{"id": "traffic_crisis", "title": "Транспортный кризис", "body": "Игрок переживает типичный день в пробках Мехико-сити. Знакомство с городскими планировщиками и инженерами.\\n", "mechanics_links": [], "assets": []}, {"id": "traditional_resistance", "title": "Сопротивление традиций", "body": "Таксисты и владельцы автобусов организуют протесты. Игрок участвует в переговорах о будущем транспорта.\\n", "mechanics_links": [], "assets": []}, {"id": "hyperloop_testing", "title": "Тестирование гиперлупа", "body": "Мини-игра: настройка подземной транспортной системы. Решение проблем безопасности и эффективности.\\n", "mechanics_links": [], "assets": []}, {"id": "transport_integration", "title": "Интеграция транспорта", "body": "Финальная демонстрация интегрированной транспортной сети. Определение будущего городских перевозок.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1150 XP.\\n- Издержки: −46 кредов.\\n- Бафф «Транспортный гений +23% эффективности в транспортных задачах на 17 часов».\\n- Ачивка «Мегаполис-координатор».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Hyperloop Urban Transport", "definition": "Высокоскоростная подземная транспортная система с магнитной левитацией для мегаполисов."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140876051, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Решения транспортных проблем мегаполиса» для Mexico City 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, descripti