INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-miami-little-havana', 'Майами — Маленькая Гавана', 'Игрок исследует кубинский квартал, общается с местными и развивает связи латино-фракций.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-miami-little-havana", "title": "Майами — Маленькая Гавана", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T13:06:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T13:06:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["miami", "cuban-culture", "gastronomy"], "topics": ["regional-content", "cultural-experience"], "related_systems": ["social-system", "economy-system"], "related_documents": [{"id": "canon-quest-miami-south-beach-neon", "relation": "complements"}, {"id": "github-issue-348", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/348", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/miami/2020-2029/quest-002-cuban-little-havana.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Экскурсия по Little Havana оставалась в Markdown и не влияла на культурные цепочки.", "goal": "Описать YAML-квест с кофейной культурой, сигарами и сальсой на Calle Ocho.", "essence": "Игрок исследует кубинский квартал, общается с местными и развивает связи латино-фракций.", "key_points": ["Включены cafecito, сигарные мастерские и Домино-парк.", "Покупка локальных товаров усиливает экономику района и культурный рейтинг.", "Квест открывает партнёрства с музыкальными и гастрономическими NPC."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 1-2 часа.\\nНаграды: 1,000 XP, -50 валюты, +15 к атрибуту «Культура», достижение «Кубинская душа».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прогуляться по Calle Ocho и зафиксировать уличные муралы.\\n2. Попробовать cafecito в местном ventanita и обсудить новости с бариста.\\n3. Посетить сигарную мастерскую, наблюдая за ручной скруткой.\\n4. Сыграть партию в домино в парке Máximo Gómez и завести новые контакты.\\n5. Завершить вечер уроком сальсы или живым концертом.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Маленькая Гавана стала домом для кубинской диаспоры после 1960-х.\\nCalle Ocho известна фестивалями, ароматом табака и музыкой сальсы.\\nРайон символизирует сохранение традиций и гостеприимство переселенцев.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест повышает репутацию среди латино-фракций и открывает гастрономические контракты.\\nМожет запускать ачивки коллекционера сигар, музыкальные дуэты и благотворительные ивенты.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Little Havana", "link": "https://en.wikipedia.org/wiki/Little_Havana"}], "decisions": []}, "implementation": {"github_issue": 348, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Перенесён культурный квест «Маленькая Гавана» в YAML и добавлены социальные механики."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONF