INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-miami-wynwood-walls', 'Майами — Wynwood Walls', 'Игрок получает разрешение на стену, создаёт собственный мурал и презентует его публике Wynwood.', 'side', 12, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "get_permission", "text": "Получить разрешение на мурал от кураторов Wynwood Walls", "type": "dialogue", "target": "wynwood_curators", "count": 1, "optional": false}, {"id": "design_sketch", "text": "Подобрать стену и разработать эскиз с учётом брендов района", "type": "interact", "target": "wall_selection", "count": 1, "optional": false}, {"id": "paint_mural", "text": "Пройти мини-игру спрей-арта, сочетая яркие тропические цвета и неон", "type": "minigame", "target": "spray_paint_minigame", "count": 1, "optional": false}, {"id": "organize_opening", "text": "Организовать открытие с приглашением блогеров и галеристов", "type": "interact", "target": "mural_opening_event", "count": 1, "optional": false}, {"id": "decide_fate", "text": "Решить судьбу работы: оставить как арт-проект или продать частной галерее", "type": "dialogue", "target": "art_gallery_npc", "count": 1, "optional": false}]'::jsonb, '{"experience": 3000, "money": 0, "reputation": {"fame": 10}, "items": [], "unlocks": {"achievements": ["Wynwood Artist"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-miami-wynwood-walls", "title": "Майами — Wynwood Walls", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.0.0", "last_updated": "2025-12-06T10:45:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T13:10:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["miami", "art", "street_culture"], "topics": ["regional-content", "creative-play"], "related_systems": ["social-system", "crafting-system"], "related_documents": [{"id": "canon-quest-miami-cuban-sandwich", "relation": "complements"}, {"id": "github-issue-349", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/349", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/miami/2020-2029/quest-004-wynwood-walls.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Квест о создании мурала в Wynwood Walls не был оформлен в структуре знаний.", "goal": "Добавить социально-художественную активность с мини-игрой спрей-арта.", "essence": "Игрок получает разрешение на стену, создаёт собственный мурал и презентует его публике Wynwood.", "key_points": ["Квест-id MIAMI-2029-004, художественная линия Майами.", "Включает подготовку эскиза, покраску и публичный релиз.", "Награда выдаёт опыт, славу и потенциальную прибыль от продажи."]}, "quest_definition": {"quest_type": "side", "level_min": 12, "level_max": null, "requirements": {"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}, "objectives": [{"id": "get_permission", "text": "Получить разрешение на мурал от кураторов Wynwood Walls", "type": "dialogue", "target": "wynwood_curators", "count": 1, "optional": false}, {"id": "design_sketch", "text": "Подобрать стену и разработать эскиз с учётом брендов района", "type": "interact", "target": "wall_selection", "count": 1, "optional": false}, {"id": "paint_mural", "text": "Пройти мини-игру спрей-арта, сочетая яркие тропические цвета и неон", "type": "minigame", "target": "spray_paint_minigame", "count": 1, "optional": false}, {"id": "organize_opening", "text": "Организовать открытие с приглашением блогеров и галеристов", "type": "interact", "target": "mural_opening_event", "count": 1, "optional": false}, {"id": "decide_fate", "text": "Решить судьбу работы: оставить как арт-проект или продать частной галерее", "type": "dialogue", "target": "art_gallery_npc", "count": 1, "optional": false}], "rewards": {"experience": 3000, "money": 0, "reputation": {"fame": 10}, "items": [], "unlocks": {"achievements": ["Wynwood Artist"], "flags": []}}, "branches": []}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: medium, формат: solo, длительность: 2-4 часа.\\nНаграды: 3,000 XP, 0–10,000 едди (продажа), +10 Fame, титул «Wynwood Artist».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Получить разрешение на мурал от кураторов Wynwood Walls.\\n2. Подобрать стену и разработать эскиз с учётом брендов района.\\n3. Пройти мини-игру спрей-арта, сочетая яркие тропические цвета и неон.\\n4. Организовать открытие с приглашением блогеров и галеристов.\\n5. Решить судьбу работы: оставить как арт-проект или продать частной галерее.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Wynwood Walls — крупнейшая outdoor галерея стрит-арта, преобразившая складской район.\\nРайон славится яркими муралами, pop-up галереями и crowdsourced арт-сценой.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Связи и механики", "body": "Квест задействует системы крафта, социального рейтинга и экономики искусства.\\nМожет открыть контракты с брендами, дополнительные стены и участие в фестивалях.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Wynwood Walls", "link": "https://thewynwoodwalls.com/"}], "decisions": []}, "implementation": {"github_issue": 1106, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован квест Wynwood Walls в YAML с художественными механиками."}, {"version": "2.0.0", "date": "2025-12-06", "author": "content_writer", "changes": "Добавлен quest_definition с полной структурой квеста согласно архитектуре системы квестов."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

