INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-atlanta-southern-hospitality-ai', 'Atlanta 2020-2029 — «Южное гостеприимство и ИИ»', 'Классическое южное гостеприимство встречается с автоматизацией, создавая уникальный гибрид сервиса.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-atlanta-southern-hospitality-ai", "title": "Atlanta 2020-2029 — «Южное гостеприимство и ИИ»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:55:30+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["atlanta", "hospitality", "ai", "southern-culture", "service"], "topics": ["culture", "technology", "service"], "related_systems": ["narrative-service", "hospitality-system", "social-system"], "related_documents": [{"id": "canon-region-america-atlanta-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/atlanta/2020-2029/quest-012-southern-hospitality-ai.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Рестораны Atlanta внедряют ИИ-консьержей, но традиционное южное гостеприимство оказывается под угрозой.", "goal": "Игрок тестирует баланс между технологической эффективностью и человеческим теплом в сфере услуг.", "essence": "Классическое южное гостеприимство встречается с автоматизацией, создавая уникальный гибрид сервиса.", "key_points": ["ИИ-официанты с южным акцентом и манерами.", "Дебаты о потере аутентичности сервиса.", "Финальный банкет с участием ИИ и людей."]}, "content": {"sections": [{"id": "ai_waitstaff", "title": "ИИ-официанты", "body": "Игрок посещает ресторан с ИИ-официантами, запрограммированными на южное гостеприимство. Знакомство с владельцем.\\n", "mechanics_links": [], "assets": []}, {"id": "authenticity_debate", "title": "Дебаты об аутентичности", "body": "Традиционные рестораторы протестуют против \\"бездушного сервиса\\". Игрок организует встречу между сторонами.\\n", "mechanics_links": [], "assets": []}, {"id": "hospitality_training", "title": "Обучение гостеприимству", "body": "Мини-игра: программирование ИИ на южные манеры и традиции. Баланс между эффективностью и человечностью.\\n", "mechanics_links": [], "assets": []}, {"id": "southern_feast", "title": "Южный пир", "body": "Финальный банкет с участием ИИ и человеческих официантов. Определение будущего ресторанного бизнеса.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 850 XP.\\n- Издержки: −35 кредов.\\n- Бафф «Гостеприимный хозяин +20% эффективности в социальных взаимодействиях на 13 часов».\\n- Ачивка «ИИ-консьерж».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "AI Hospitality", "definition": "Искусственный интеллект, запрограммированный на предоставление персонализированного сервиса с учетом культурных традиций."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140875691, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Южное гостеприимство и ИИ» для Atlanta 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCL