INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-aztec-heritage', 'Мехико — Ацтекское наследие', 'Игрок посещает музей Templo Mayor, изучает легенду об орле на кактусе и формирует отношение к конкисте.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-aztec-heritage", "title": "Мехико — Ацтекское наследие", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T13:01:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T13:01:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["mexico_city", "history", "archaeology"], "topics": ["regional-content", "ancient-history"], "related_systems": ["quest-system", "exploration-system"], "related_documents": [{"id": "canon-quest-mexico-city-zocalo", "relation": "complements"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/mexico-city/2020-2029/quest-009-aztec-heritage.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "История Теночтитлана и Templo Mayor не была описана в структуре знаний.", "goal": "Создать археологический квест, связывающий руины под центром Мехико с учебными цепочками.", "essence": "Игрок посещает музей Templo Mayor, изучает легенду об орле на кактусе и формирует отношение к конкисте.", "key_points": ["Включены основные объекты Теночтитлана и экспозиции черепных стен.", "Игрок решает, как реагировать на наследие завоеваний.", "Награда усиливает археологические навыки и открывает экскурсионные маршруты."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main, сложность: medium, формат: solo, длительность: 2-4 часа.\\nНаграды: 3,000 XP, -10 валюты, +25 к атрибуту «Археология», достижение «Наследник Ацтеков».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Добраться до музея Templo Mayor рядом с Zócalo.\\n2. Осмотреть руины храма, в том числе tzompantli и жертвенники.\\n3. Изучить экспонаты о легенде основания Теночтитлана.\\n4. Сравнить хроники ацтеков и конкистадоров в интерактивной зале.\\n5. Принять позицию: почтить наследие или осудить разрушение города.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Современный Мехико построен на руинах Теночтитлана, столицы империи, существовавшей с XIV по XVI век.\\nСимвол орла на кактусе до сих пор украшает флаг Мексики.\\nРаскопки Templo Mayor продолжаются и сегодня, открывая новые артефакты.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест включает археологические экспедиции и доступ к научным контактам.\\nМожет активировать образовательные мероприятия и выставки для музейных фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Templo Mayor Museum", "link": "https://en.wikipedia.org/wiki/Templo_Mayor"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован исторический квест «Ацтекское наследие» в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

