INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-mexico-city-altitude-sickness', 'Мехико — Высотная адаптация', 'Игрок прибывает в столицу на высоте 2 240 м, борется с нехваткой кислорода и учится уменьшать дебаффы.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-mexico-city-altitude-sickness", "title": "Мехико — Высотная адаптация", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T13:03:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T13:03:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["mexico_city", "survival", "health"], "topics": ["regional-content", "physiology"], "related_systems": ["survival-system", "buff-system"], "related_documents": [{"id": "canon-quest-mexico-city-teotihuacan-pyramids", "relation": "references"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/mexico-city/2020-2029/quest-010-altitude-sickness.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Сценарий высотной адаптации игроков в Мехико сохранялся в Markdown и не запускал механики выживания.", "goal": "Описать YAML-квест, объясняющий симптомы soroche и способы безопасной акклиматизации.", "essence": "Игрок прибывает в столицу на высоте 2 240 м, борется с нехваткой кислорода и учится уменьшать дебаффы.", "key_points": ["Пошаговый план адаптации на первые двое суток.", "Разовые затраты на медикаменты и гидратацию окупаются бонусами к выносливости.", "Квест запускает систему климатических дебаффов для других высокогорных зон."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side, сложность: hard, формат: solo, длительность: 1-2 дня.\\nНаграды: 2,000 XP, 0 валюты, +20 к атрибуту «Выносливость», временный дебафф «Soroche»: -30% Stamina на 3 дня.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прибыть в Мехико и ощутить симптомы высоты (головная боль, одышка, усталость).\\n2. Посетить клинику для экспресс-диагностики и получить рекомендации по гидратации.\\n3. Провести день в спокойном режиме, избегая алкоголя и перегрузок.\\n4. На второй день начать лёгкие прогулки и отслеживать показатели кислорода.\\n5. Закрепить адаптацию дыхательными упражнениями и витаминами.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Мехико расположен на высоте около 2 240 метров над уровнем моря — заметно выше большинства мегаполисов.\\nРазреженный воздух содержит на 20–25% меньше кислорода, что вызывает soroche у туристов.\\nГородские службы распространяют инструкции по плавной акклиматизации.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест активирует климатические дебаффы и бонусы выживания для высокогорных зон кампании.\\nМожет запускать ежедневные задания на мониторинг здоровья и доступ к аптечным поставщикам.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "High-altitude acclimatization", "link": "https://en.wikipedia.org/wiki/Altitude_sickness"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован квест о высотной адаптации в YAML и добавлены климатические механики."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

