INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-miami-speedboat-race', 'Майами — Гонки на спидботах', 'Игрок модернизирует спидбот и участвует в нелегальной гонке по Biscayne Bay, соревнуясь за легендарное судно.', 'side', 15, NULL, '{"required_quests": [], "required_flags": [], "required_reputation": {}, "required_items": []}'::jsonb, '[{"id": "register_race", "text": "Зарегистрироваться на ночную гонку и внести вступительный взнос 5,000 €$", "type": "interact", "target": "race_registration", "count": 1, "optional": false}, {"id": "upgrade_boat", "text": "Модернизировать лодку: усиленный двигатель, стабилизаторы, дополнительное топливо", "type": "interact", "target": "boat_upgrade_station", "count": 1, "optional": false}, {"id": "complete_race", "text": "Пройти 10 кругов гонки, избегая волн и столкновений", "type": "minigame", "target": "speedboat_race_track", "count": 10, "optional": false}, {"id": "finish_race", "text": "Финишировать у South Beach и пройти проверку на победителя", "type": "travel", "target": "south_beach_finish", "count": 1, "optional": false}]'::jsonb, '{"experience": 2000, "money": 50000, "reputation": {"water_sports": 20}, "items": ["legendary_speedboat"], "unlocks": {"achievements": ["Спидбот-чемпион Biscayne Bay"], "flags": []}}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-miami-speedboat-race", "title": "Майами — Гонки на спидботах", "document_type": "canon", "category": "quest", "status": "draft", "version": "2.1.0", "last_updated": "2025-12-12T00:00:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T15:05:00+00:00", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["miami", "speedboat", "racing"], "topics": ["water-sports", "illicit-competition"], "related_systems": ["vehicle-system", "reputation-system"], "related_docume