-- Quest: canon-quest-ny-pizza-rat
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-pizza-rat', 'Нью-Йорк — Пиццевая крыса', 'Игрок выслеживает легендарную пиццерию в Бруклине, знакомится с владельцем и пробует «настоящий» slice, возвращая вкус прошлого.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-pizza-rat", "title": "Нью-Йорк — Пиццевая крыса", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:08:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:08:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "pizza", "nostalgia"], "topics": ["slice-of-life", "gastronomy", "community"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "social-service", "culture-service", "wellbeing-service", "reputation-service"], "related_documents": [{"id": "github-issue-352", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/352", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2020-2029/quest-004-pizza-rat.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Гастрономический квест «Pizza Rat» существовал в Markdown и не описывал системные баффы и репутационные эффекты.", "goal": "Структурировать поиск настоящей нью-йоркской пиццы в YAML, подчеркнув мемы и культурные связи.", "essence": "Игрок выслеживает легендарную пиццерию в Бруклине, знакомится с владельцем и пробует «настоящий» slice, возвращая вкус прошлого.", "key_points": ["Этапы слухов, поиска и дегустации объединяют мем «Pizza Rat» с итальянскими традициями.", "Добавлены системные крючки для food- и mood-system, влияющие на здоровье и ностальгию.", "Награды открывают кулинарную ветку и титул «Настоящий Нью-Йоркец»."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: easy. Формат: solo. Длительность: 30–60 минут.\\nНаграды: 600 XP, бафф «Пицца Нью-Йорк» (+25% HP на 8 часов), титул «Настоящий Нью-Йоркец», расходы 15 едди.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Собрать слухи о пиццерии, где используют настоящую дровяную печь и живую моцареллу.\\n2. Найти скрытый вход в бруклинский переулок, наблюдать за очередью местных.\\n3. Побеседовать с пожилым владельцем, узнать историю семьи и мем «Pizza Rat» 2015 года.\\n4. Дегустировать классический slice, измерить показатели food-system и активировать бафф.\\n5. Решить, делиться ли локацией в сетях или сохранить секрет, влияя на репутацию у гурманов.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "food-system рассчитывает бафф здоровья и насыщение, mood-system увеличивает ностальгию и счастье.\\nreputation-system отражает отношение кулинарных сообществ и влияет на скидки в пиццериях.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Подчёркнуты дровяная печь, красные клетчатые скатерти, запах базилика и итальянский сленг «Fuhgeddaboudit».\\nОписана атмосфера нью-йоркских улиц и отсылки к вирусному видео Pizza Rat.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Награды включают 600 XP, бафф +25% HP и титул, а также скидки в заведении.\\nРазглашение секрета без согласия владельца снижает доверие и закрывает доступ к скрытому меню.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "History of New York Pizza Culture", "link": "https://www.eater.com"}, {"title": "Pizza Rat Meme Archive", "link": "https://knowyourmeme.com"}], "decisions": []}, "implementation": {"github_issue": 352, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Конвертировано в шаблон 1.1.0, добавлены системные крючки и гастрономические награды."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание квеста Pizza Rat."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

