INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-miami-south-beach-neon', 'Майами — Неон Саут-Бич', 'Игрок бродит по Саут-Бич, наслаждается неоновыми огнями и решает, куда вложить туристический бюджет.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-miami-south-beach-neon", "title": "Майами — Неон Саут-Бич", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T13:05:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T13:05:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["miami", "tourism", "nightlife"], "topics": ["regional-content", "art-deco"], "related_systems": ["social-system", "economy-system"], "related_documents": [{"id": "canon-quest-miami-little-havana", "relation": "complements"}, {"id": "github-issue-348", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/348", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/miami/2020-2029/quest-001-south-beach-neon.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "low"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Маршрут по South Beach был в Markdown и не активировал туристические механики.", "goal": "Перевести прогулку по Ocean Drive и району Art Deco в YAML с вечерними событиями.", "essence": "Игрок бродит по Саут-Бич, наслаждается неоновыми огнями и решает, куда вложить туристический бюджет.", "key_points": ["Подчёркнута атмосфера Art Deco и вечеринок Ocean Drive.", "Расходы на клубы и напитки увеличивают стиль и социальные связи.", "Квест открывает ночные активности и бонусы для туристических фракций."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social, сложность: easy, формат: solo, длительность: 1-2 часа.\\nНаграды: 800 XP, -100 валюты, +15 к атрибуту «Стиль», достижение «Vice City Vibes».\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Прогуляться по Ocean Drive днём и отметить ключевые здания Art Deco.\\n2. Перейти на пляж к закату и сделать фотоснимки с пальмами и неоном.\\n3. Забронировать столик в баре и послушать живой латинский сет.\\n4. Посетить ночной клуб или уличную вечеринку для повышения социальной репутации.\\n5. Завершить ночь на пляже или в лаундж-зоне, оценив затраты и настроение группы.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональные детали", "body": "Саут-Бич славится более чем 800 зданиями Art Deco в пастельных тонах.\\nНеоновые вывески и пальмы формируют узнаваемый визуальный стиль Майами.\\nРайон считается центром ночной жизни и иконой ретро-неона.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "Квест повышает туристическую экономику и открывает партнёрства с клубами и барами.\\nМожет запускать тематические ивенты с DJ, модными домами и рекламой.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "South Beach Art Deco District", "link": "https://en.wikipedia.org/wiki/South_Beach"}], "decisions": []}, "implementation": {"github_issue": 348, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Создан туристический квест о неоне Саут-Бич в формате YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFL