-- Quest: canon-quest-ny-subway-survival
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-subway-survival', 'Нью-Йорк — Выживание в метро', 'Команда спускается в заброшенные туннели Нью-Йорка, сталкивается с бандами и мутантами-крысами и выбирается через альтернативную станцию.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-subway-survival", "title": "Нью-Йорк — Выживание в метро", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T17:09:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T17:09:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "subway", "survival"], "topics": ["underground-exploration", "cooperative-combat", "horror"], "related_systems": ["narrative-service", "gameplay-service", "quest-service", "character-service", "economy-service", "survival-service", "combat-service", "exploration-service", "stealth-service"], "related_documents": [{"id": "canon-quest-ny-times-square-chase", "relation": "parallels"}, {"id": "github-issue-352", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/352", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2020-2029/quest-005-subway-survival.md", "visibility": "internal", "audience": ["narrative", "systems", "gameplay"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Описание туннельного квеста метро хранилось в Markdown и не описывало системные опасности и награды.", "goal": "Структурировать выживание в метро в YAML, подчеркнув кооператив, мутантов и навигацию.", "essence": "Команда спускается в заброшенные туннели Нью-Йорка, сталкивается с бандами и мутантами-крысами и выбирается через альтернативную станцию.", "key_points": ["Детализированы этапы проникновения, навигации и столкновений с врагами.", "Добавлены системные крючки для survival- и combat-system, влияющие на ресурсы и угрозы.", "Награды включают редкие компоненты и продвижение подземной сюжетной линии."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side. Сложность: hard. Формат: group 2–4. Длительность: 2–4 часа.\\nНаграды: 2 000 XP, редкие компоненты с врагов, улучшение отношений с подземными фракциями.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Спуститься в заброшенный вход метро и подготовить маршруты отхода.\\n2. Использовать карту или память, чтобы ориентироваться в лабиринте из 472 станций.\\n3. Переговорить с подземными бандами «Mole People» или вступить в бой за доступ к путям.\\n4. Отразить атаку мутировавших крыс, защитив генератор света и третий рельс.\\n5. Добраться до безопасной станции и эвакуироваться, избегая электризированных участков.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "survival-system отслеживает здоровье, стресс и ресурсы, combat-system регулирует PvE-бои с бандами и мутантами.\\nnavigation-system обрабатывает ветвления маршрутов и вероятность заблудиться, влияя на время квеста.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны граффити на стенах, ржавые составы, объявление «Stand clear of the closing doors» и лагеря бездомных.\\nУказаны опасности третьего рельса и легенды о гигантских крысах метро.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игроки получают 2 000 XP, редкие компоненты и доступ к подземным сделкам; провал приводит к потере лута и ранению союзников.\\nУспех укрепляет репутацию у подземных фракций и открывает маршруты для контрабанды.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "NYC Subway History", "link": "https://www.nycsubway.org"}, {"title": "Urban Legends of Mole People", "link": "https://www.atlasobscura.com"}], "decisions": []}, "implementation": {"github_issue": 352, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Переведено в шаблон 1.1.0, добавлены системные крючки и награды выживания."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание выживания в метро."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

