-- Quest: canon-quest-miami-drug-run
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-miami-drug-run', 'Майами — Наркоперевозка', 'Игрок принимает заказ картеля, прорывается на скоростном катере к побережью Майами и решает судьбу груза между картелем и DEA.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-miami-drug-run", "title": "Майами — Наркоперевозка", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.0.0", "last_updated": "2025-11-06T13:05:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-06T13:05:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["miami", "smuggling", "crime"], "topics": ["regional-content", "organized-crime"], "related_systems": ["quest-system", "vehicle-system", "morality-system"], "related_documents": [{"id": "canon-detailed-events-2020-2030", "relation": "references"}, {"id": "github-issue-348", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/348", "relation": "migrated_to", "migrated_at": "2025-11-22T20:30:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/miami/2020-2029/quest-003-drug-run.md", "visibility": "internal", "audience": ["lore", "narrative", "quest-design"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Криминальный квест про морскую контрабанду Майами находился в Markdown и не был привязан к системам.", "goal": "Перевести сцену контрабандного рейса в YAML с ветками выбора и интеграцией транспортных механик.", "essence": "Игрок принимает заказ картеля, прорывается на скоростном катере к побережью Майами и решает судьбу груза между картелем и DEA.", "key_points": ["Квест-id MIAMI-2029-003, основной криминальный сценарий Майами.", {"Три исхода": "доставка картелю, передача DEA или присвоение груза."}, "Награды влияют на репутацию и розыск у фракций."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: main, сложность: extreme, формат: группа 2-4 игроков, длительность: 4+ часов.\\nНаграды по веткам: \\n  • Доставка — 15,000 XP, 500,000 едди, репутация картеля +20.\\n  • Передача DEA — 10,000 XP, 200,000 едди, защита DEA.\\n  • Присвоение — 5,000 XP, 1,000,000 едди, розыск картеля.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Встретиться с представителем картеля и подписать контракт на перевозку.\\n2. Провести загрузку контейнеров на скоростной катер на Багамах.\\n3. Выполнить ночной переход Багамы → Майами, обходя патрули Coast Guard.\\n4. Уклониться от вертолётов и катеров береговой охраны с использованием дымовых завес и манёвров.\\n5. Доставить груз на тайный причал или выбрать альтернативный исход.\\n", "mechanics_links": [], "assets": []}, {"id": "branches", "title": "Варианты развязки", "body": "• Выполнить заказ картеля и получить доверие криминальной сети.\\n• Предать картель DEA, активировав защиту свидетеля и денежную награду.\\n• Оставить груз себе, что запускает цепочку охоты картеля.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_context", "title": "Региональный контекст", "body": "Майами десятилетиями служит воротами наркотрафика; скоростные катера и вертолёты Coast Guard — часть городской легенды.\\nОтсылки к \\"Cocaine Cowboys\\" и кинематографу 1980-х поддерживают атмосферу.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Связи и механики", "body": "Квест задействует системы морского транспорта, скрытности и моральных выборов.\\nВетви открывают дополнительные цепочки: работа на картель, сотрудничество с DEA или бегство от обоих.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Cocaine Cowboys", "link": "https://en.wikipedia.org/wiki/Cocaine_Cowboys"}, {"title": "Coast Guard Fast Response", "link": "https://www.uscg.mil/"}], "decisions": []}, "implementation": {"github_issue": 348, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.0.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Конвертирован квест морской контрабанды Майами в YAML."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

