-- Quest: quest-boston-2029-003-harvard-mit
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('quest-boston-2029-003-harvard-mit', 'Бостон — Гарвард и MIT', 'Игрок исследует кампусы двух ведущих университетов мира и получает бонус к интеллекту и социальным контактам.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "quest-boston-2029-003-harvard-mit", "title": "Бостон — Гарвард и MIT", "document_type": "canon", "category": "narrative", "status": "approved", "version": "0.1.0", "last_updated": "2025-11-22T19:20:00+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "concept_director", "contact": "concept@necp.game"}], "tags": ["boston", "harvard", "mit"], "topics": ["timeline_author", "education"], "related_systems": ["narrative-service", "education-service", "social-service"], "related_documents": [{"id": "github-issue-300", "title": "GitHub Issue", "link": "https://github.com/gc-lover/necpgame-monorepo/issues/300", "relation": "migrated_to", "migrated_at": "2025-11-22T19:20:00+00:00"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/boston/2020-2029/quest-003-harvard-mit.md", "visibility": "internal", "audience": ["concept", "narrative", "education"], "risk_level": "low"}, "review": {"chain": [{"role": "concept_director", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Образовательные достопримечательности Бостона не описаны в каноне и не задействованы в прогрессии знаний и социальных связях.", "goal": "Структурировать квест посещения Гарварда и MIT для дальнейшей интеграции в образовательные и социальные цепочки.", "essence": "Игрок исследует кампусы двух ведущих университетов мира и получает бонус к интеллекту и социальным контактам.", "key_points": ["Harvard Yard и MIT Infinite Corridor демонстрируют контраст традиций и технологий.", "Регион позиционируется как «college town» с более чем 60 университетами.", "Традиция «потереть ногу» статуи Джона Гарварда добавляет социальный flavor.", "Квест усиливает образовательную линию и может активировать академические ветки."]}, "content": {"sections": [{"id": "overview", "title": "Описание", "body": "Квест знакомит игрока с академическим динамизмом Бостона: он посещает Harvard University и Massachusetts Institute\\nof Technology, узнаёт об истории учреждений и участвует в локальных традициях.\\n", "mechanics_links": []}, {"id": "regional_highlights", "title": "Региональные отсылки", "body": "- Harvard основан в 1636 году и считается старейшим университетом США.\\n- MIT славится инновациями и культовым Infinite Corridor.\\n- Бостон — один из крупнейших студенческих городов мира с десятками кампусов.\\n- Ритуал натирания ноги статуи Джона Гарварда приносит удачу первокурсникам.\\n", "mechanics_links": []}, {"id": "quest_flow", "title": "Этапы прохождения", "body": "1. Войти в Harvard Yard, посетить Widener Library и Memorial Church.\\n2. Встретиться с NPC-студентами и обменяться знаниями.\\n3. Перейти через реку и исследовать MIT: Infinite Corridor, главный купол и арт-объекты.\\n4. Завершить визит обсуждением роли университетов в мире NECPGAME.\\n", "mechanics_links": []}, {"id": "rewards", "title": "Награды и эффекты", "body": "Квест приносит 2000 опыта, добавляет +20 к показателю «Интеллект» и не изменяет валютный баланс. Открывается\\nдостижение «Студент Лиги Плюща» и дополнительные ветки стажировок.\\n", "mechanics_links": []}, {"id": "mechanics", "title": "Механики и темы", "body": "Образовательная и туристическая механика: игрок собирает знания, повышает социальный капитал и может открыть\\nдоступ к академическим событиям.\\n", "mechanics_links": []}, {"id": "connections", "title": "Связи и расширения", "body": "Квест поддерживает образовательную линию Бостона и может вести к совместным заданиям с исследовательскими\\nкорпорациями, студенческими организациями и инженерными гильдиями.\\n", "mechanics_links": []}]}, "appendix": {"glossary": [], "references": [], "decisions": []}, "implementation": {"github_issue": 300, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-11-11", "author": "concept_director", "changes": "Конверсия квеста «Гарвард и MIT» в YAML-формат."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

