-- Quest: canon-quest-ny-harlem-renaissance
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-harlem-renaissance', 'Нью-Йорк — Ренессанс Гарлема', 'Игрок знакомится с современным Гарлемом, выступает на сцене Apollo Theater и укрепляет связи с местным сообществом.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-harlem-renaissance", "title": "Нью-Йорк — Ренессанс Гарлема", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T16:53:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T16:53:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "harlem", "culture"], "topics": ["cultural-immersion", "community", "music"], "related_systems": ["reputation-system", "mood-system", "event-system"], "related_documents": [{"id": "canon-quest-ny-hip-hop-battle", "relation": "leads_to"}], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-014-harlem-renaissance.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "medium"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Материал о возрождении Гарлема находился в Markdown и не описывал влияние на сообщества и системные награды.", "goal": "Структурировать квест в YAML, выделив культурные события, социальные связи и эффекты mood-system.", "essence": "Игрок знакомится с современным Гарлемом, выступает на сцене Apollo Theater и укрепляет связи с местным сообществом.", "key_points": ["Описаны этапы посещения iconic-локаций, выступлений и общения с жителями.", "Добавлены системные крючки для mood- и reputation-system, влияющие на культурные ветки города.", "Награды открывают soul food бонусы и музыкальные события, связывая квест с хип-хоп сценой."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: social. Сложность: medium. Формат: solo. Длительность: 2–4 часа.\\nНаграды: 1 500 XP, «Друг Гарлема», доступ к культурным ивентам района, расходы 1 000 едди.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Посетить Apollo Theater, зарегистрироваться на Amateur Night и познакомиться с куратором сцены.\\n2. Выступить на открытом микрофоне, завоевать поддержку аудитории и наставников.\\n3. Отправиться в soul food ресторан, активировать гастрономический бафф и получить рекомендации от шефа.\\n4. Принять участие в воскресном служении с gospel-хором, выбрать стиль взаимодействия с общиной.\\n5. Побеседовать со старожилами, записать их истории и получить приглашения на будущие события Гарлема.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "mood-system усиливает вдохновение и открывает музыкальные импровизации, reputation-system фиксирует отношения с культурными лидерами.\\nevent-system создаёт линейку уникальных концертов и сотрудничеств для последующих квестов Нью-Йорка.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны brownstone-дома, уличные джазовые ансамбли, gospel-хоры и сленг местных жителей.\\nПодчёркнута история Harlem Renaissance 1920-х и её современное продолжение в 2030-х.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Игрок получает титул «Друг Гарлема», открывает скидки в soul food заведениях и приглашения на культурные события.\\nИгнорирование местных традиций снижает доверие и блокирует доступ к коллаборациям с хип-хоп сценой.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Harlem Renaissance History", "link": "https://www.smithsonianmag.com"}, {"title": "Apollo Theater Amateur Night", "link": "https://www.apollotheater.org"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Добавлены культурные крючки, системные эффекты и связь с хип-хоп линией."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-описание культурного маршрута Гарлема."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

