INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-miami-crypto-blockchain-revolution', 'Miami 2020-2029 — «Крипто-блокчейн революция»', 'Майами как рай для крипто-энтузиастов развивает технологии, которые меняют финансовую систему мира.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-miami-crypto-blockchain-revolution", "title": "Miami 2020-2029 — «Крипто-блокчейн революция»", "document_type": "canon", "category": "timeline-author", "status": "draft", "version": "0.1.0", "last_updated": "2025-12-14T11:59:46+00:00", "concept_approved": false, "concept_reviewed_at": "", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["miami", "crypto", "blockchain", "finance", "revolution"], "topics": ["crypto", "finance", "technology"], "related_systems": ["narrative-service", "finance-system", "crypto-system"], "related_documents": [{"id": "canon-region-america-miami-2020-2029", "relation": "contextualizes"}], "source": "shared/docs/knowledge/canon/lore/_03-lore/timeline-author/quests/america/miami/2020-2029/quest-012-crypto-blockchain-revolution.md", "visibility": "internal", "audience": ["narrative", "quest-design", "live_ops"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "Майами становится центром крипто-революции с блокчейн-технологиями для децентрализованного управления городом, но сталкивается с регуляторными барьерами.", "goal": "Игрок помогает внедрить крипто-технологии в городскую инфраструктуру и преодолевает сопротивление традиционных финансовых институтов.", "essence": "Майами как рай для крипто-энтузиастов развивает технологии, которые меняют финансовую систему мира.", "key_points": ["DAO для управления городскими сервисами.", "Крипто-валюты для оплаты коммунальных услуг.", "Финальная интеграция блокчейна в городскую экономику."]}, "content": {"sections": [{"id": "crypto_conference", "title": "Крипто-конференция", "body": "Игрок посещает крупнейшую в мире крипто-конференцию в Майами. Знакомство с блокчейн-инноваторами и предпринимателями.\\n", "mechanics_links": [], "assets": []}, {"id": "regulatory_hurdles", "title": "Регуляторные барьеры", "body": "Правительственные агентства пытаются ограничить развитие крипто-технологий. Игрок участвует в переговорах о регулировании.\\n", "mechanics_links": [], "assets": []}, {"id": "blockchain_voting", "title": "Блокчейн-голосование", "body": "Мини-игра: настройка системы DAO-голосования для городских решений. Обеспечение безопасности и прозрачности.\\n", "mechanics_links": [], "assets": []}, {"id": "crypto_city_summit", "title": "Саммит Крипто-города", "body": "Финальная презентация Майами как первого полностью крипто-интегрированного города. Определение будущего финансов.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды", "body": "- 1200 XP.\\n- Издержки: −48 кредов.\\n- Бафф «Крипто-магнат +25% эффективности в финансовых задачах на 16 часов».\\n- Ачивка «Блокчейн-пионер».\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [{"term": "Crypto City Infrastructure", "definition": "Городская инфраструктура, интегрированная с блокчейн-технологиями для децентрализованного управления и оплаты услуг."}], "references": [], "decisions": []}, "implementation": {"github_issue": 140876051, "needs_task": false, "queue_reference": [], "blockers": []}, "history": [{"version": "0.1.0", "date": "2025-12-14", "author": "content_agent", "changes": "Создание квеста «Крипто-блокчейн революция» для Miami 2020-2029."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

