-- Quest: canon-quest-ny-moma-heist
INSERT INTO gameplay.quest_definitions (quest_id, title, description, quest_type, level_min, level_max, requirements, objectives, rewards, branches, content_data, version, is_active) VALUES ('canon-quest-ny-moma-heist', 'Нью-Йорк — Ограбление МоМА', 'Команда взломщиков проникает в MoMA, обходя охрану мирового уровня, похищает выбранный шедевр и скрывается из Мидтауна.', 'side', NULL, NULL, '{}'::jsonb, '[]'::jsonb, '{}'::jsonb, '[]'::jsonb, '{"metadata": {"id": "canon-quest-ny-moma-heist", "title": "Нью-Йорк — Ограбление МоМА", "document_type": "canon", "category": "quest", "status": "approved", "version": "1.1.0", "last_updated": "2025-11-11T16:58:00+00:00", "concept_approved": true, "concept_reviewed_at": "2025-11-11T16:58:00+00:00", "owners": [{"role": "narrative_team", "contact": "narrative@necp.game"}], "tags": ["new_york", "museum", "heist"], "topics": ["crime", "culture", "stealth"], "related_systems": ["infiltration-system", "stealth-system", "economy-system"], "related_documents": [], "source": "shared/docs/knowledge/canon/lore/timeline-author/quests/america/new-york/2030-2039/quest-016-moma-heist.md", "visibility": "internal", "audience": ["narrative", "content", "systems"], "risk_level": "high"}, "review": {"chain": [{"role": "narrative_lead", "reviewer": "", "reviewed_at": "", "status": "pending"}], "next_actions": []}, "summary": {"problem": "План ограбления МоМА существовал в Markdown и не описывал системные риски и награды.", "goal": "Структурировать криминальную операцию в музее, связав её с системами стелса, экономики и репутации.", "essence": "Команда взломщиков проникает в MoMA, обходя охрану мирового уровня, похищает выбранный шедевр и скрывается из Мидтауна.", "key_points": ["Описаны этапы подготовки, проникновения и эвакуации с произведением искусства.", "Добавлены крючки для infiltration- и stealth-system, учитывающие сенсоры и социальную инженерию.", "Награды включают продажи шедевра и последствия в виде федерального розыска."]}, "content": {"sections": [{"id": "quest_profile", "title": "Параметры квеста", "body": "Тип: side. Сложность: extreme. Формат: group 2–4. Длительность: 4+ часов.\\nНаграды: 5 000 XP, 200 000 едди за продажу шедевра, легендарный арт-лут, рост криминальной репутации.\\n", "mechanics_links": [], "assets": []}, {"id": "stages", "title": "Этапы", "body": "1. Провести разведку MoMA, изучить график охраны и выбрать цель (Van Gogh, Picasso, Warhol).\\n2. Подготовить легенды, доступы и оборудование для обхода лазерных решёток и сенсоров.\\n3. Проникнуть в музей через сервисные коридоры или с помощью социальной инженерии.\\n4. Обезвредить сигнализацию, извлечь полотно и транспортировать его до выхода без повреждений.\\n5. Эвакуироваться по запасному маршруту, скрывая следы и уходя от преследования.\\n", "mechanics_links": [], "assets": []}, {"id": "systems_hooks", "title": "Системные крючки", "body": "infiltration-system моделирует доступы, легенды и взаимодействие с охраной.\\nstealth-system обрабатывает движение через лазеры, датчики и камеры; economy-system управляет сбытом и отмыванием прибыли.\\n", "mechanics_links": [], "assets": []}, {"id": "regional_details", "title": "Региональные детали", "body": "Описаны залы MoMA, лазерные решётки вокруг шедевров, ночные патрули и центральное расположение в Midtown Manhattan.\\nПодчёркнут контраст между современным искусством, туристами и тенью подпольных коллекционеров.\\n", "mechanics_links": [], "assets": []}, {"id": "rewards", "title": "Награды и последствия", "body": "Успех приносит 200 000 едди, уникальное произведение искусства и доступ к элите криминальных заказчиков.\\nПровал активирует федеральный розыск, штрафы и снижение репутации среди культурных фракций.\\n", "mechanics_links": [], "assets": []}]}, "appendix": {"glossary": [], "references": [{"title": "Museum of Modern Art Security Overview", "link": "https://www.moma.org"}, {"title": "Famous Art Heists Timeline", "link": "https://www.smithsonianmag.com"}], "decisions": []}, "implementation": {"github_issue": 132, "needs_task": false, "queue_reference": ["shared/trackers/queues/concept/queued.yaml"], "blockers": []}, "history": [{"version": "1.1.0", "date": "2025-11-11", "author": "narrative_team", "changes": "Переведено в актуальный YAML, добавлены системные крючки, награды и последствия."}, {"version": "0.1.0", "date": "2025-11-06", "author": "narrative_team", "changes": "Markdown-концепт ограбления MoMA."}], "validation": {"checksum": "", "schema_version": "1.0"}}'::jsonb, 1, true) ON CONFLICT (quest_id) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, quest_type = EXCLUDED.quest_type, level_min = EXCLUDED.level_min, level_max = EXCLUDED.level_max, requirements = EXCLUDED.requirements, objectives = EXCLUDED.objectives, rewards = EXCLUDED.rewards, branches = EXCLUDED.branches, content_data = EXCLUDED.content_data, updated_at = CURRENT_TIMESTAMP;

